export type TriggerTypes = 'hover' | 'click' | 'contextmenu' | 'focus' | 'key';

export interface OverlayTriggerEvent {
  type: TriggerTypes;
  nativeEvent: any;
}

export interface OverlayTriggerProps {
  /**
   * The children to be rendered within the overlay trigger.
