export const Avatar: FC<AvatarProps> & AvatarRef = forwardRef<
  HTMLDivElement,
  AvatarProps
>(
  (
    {
      name,
      src,
      color,
      size = 24,
      variant = 'filled',
      rounded = true,
      className,
      colorOptions,
      theme: customTheme,
      ...rest
    },
    ref
  ) => {
    const fontSize = size * 0.4;

    const initials = useMemo(() => getInitials(name || ''), [name]);

    const backgroundColor = useMemo(() => {
      if (src) {
        return 'transparent';
      }

      if (color) {
        return color;
      }

      return generateColor(name || '', colorOptions);
    }, [color, name, src, colorOptions]);

    const theme: AvatarTheme = useComponentTheme('avatar', customTheme);

    return (
      <div
        {...rest}
        className={twMerge(theme.base, rounded && theme.rounded, className)}
        style={{
          width: `${size}px`,
          height: `${size}px`,
          fontSize: `${fontSize}px`,
          backgroundImage: src ? `url(${src})` : 'none',
          backgroundColor,
          ...(variant === 'outline' && {
            backgroundColor: 'transparent',
            border: `solid 1px ${backgroundColor}`,
            color: backgroundColor
          })
        }}
        ref={ref}
      >
        {!src && name && <span>{initials}</span>}
      </div>
    );
  }
);
