  return (
    <span
      title={allowToggle ? tooltipText : undefined}
      role="button"
      className={twMerge(
        theme.base,
        allowToggle && theme.interactive,
        className
      )}
      onClick={() => allowToggle && setVisible(!visible)}
    >
      {visible ? value : masked}
