} from 'react';
import { CommandPaletteInput } from './CommandPaletteInput';
import {
  DATA_ATTRIBUTE_INDEX,
  HotkeyIem,
  useFlattenedTree
} from './useFlattenedTree';
import { List, ListItem } from '@/layout/List';
