type DeepPartial<T> = {
  [P in keyof T]?: DeepPartial<T[P]>;
};

export type PartialReablocksTheme = DeepPartial<ReablocksTheme>;

/**
 * Performs a merge deep on the theme.
 */
export const extendTheme = (
  defaultTheme: ReablocksTheme,
  theme: PartialReablocksTheme
) => {
  return mergeDeep(defaultTheme, theme);
};
import { mergeDeep } from '@/utils/Theme/helpers';

type DeepPartial<T> = {
  [P in keyof T]?: DeepPartial<T[P]>;
};

/**
 * Performs a merge deep on the component theme.
 */

export const extendComponentTheme = <T extends Object>(
  defaultTheme: T,
  theme: DeepPartial<T>
) => {
  return mergeDeep(defaultTheme, theme);
};
/**
 * Given a theme, extract the things we need for the theme builder.
 */
export function extractTheme(config) {
  const colors = { ...config.colors };

  // Delete simple things
  delete colors['inherit'];
  delete colors['transparent'];
  delete colors['current'];

  return {
    colors,
    borderRadius: config.borderRadius,
    boxShadow: config.boxShadow,
    spacing: config.spacing,
    fontFamily: config.fontFamily,
    fontSize: config.fontSize,
    fontWeight: config.fontWeight
  };
}
