import { cloneElement, useMemo } from 'react';
import classNames from 'classnames';

interface CloneElementProps {
  element: any;
  children?: any;
  childRef?: any;
}

/**
 * CloneElement is a wrapper component for createElement function.
 * This allows you to describe your cloning element declaratively
 * which is a more natural API for React.
 */
export function CloneElement<T = any>({
  children,
  element,
  childRef,
  ...rest
}: CloneElementProps & Partial<T>) {
  const getProjectedProps = useMemo(
    () => props => {
      const childProps = element.props;

      return Object.keys(props).reduce((acc, key) => {
        const prop = props[key];
        const childProp = childProps[key];

        if (typeof prop === 'function' && typeof childProp === 'function') {
          acc[key] = (...args) => {
            prop(...args);
            childProp(...args);
          };
        } else if (key === 'className') {
          acc[key] = classNames(prop, childProp);
        } else {
          acc[key] = prop;
        }

        return acc;
      }, {});
    },
    [rest]
  );

  if (element === null) {
    return children;
  }

  // Tricky logic around functional vs class components
  const ref = childRef
    ? node => {
        if (typeof childRef === 'function') {
          childRef(node);
        } else if (ref) {
          childRef.current = node;
        }
      }
    : undefined;

  const newProps = getProjectedProps(rest);
  return cloneElement(element, {
    ...element.props,
    ...newProps,
    children,
    ref
  });
}
import { useEffect } from 'react';

/**
 * Set the body select style to none.
 */
export function useUserSelect(active: boolean) {
  useEffect(() => {
    if (active) {
      document.body.style.userSelect = 'none';
      return () => void (document.body.style.userSelect = 'initial');
    }
  }, [active]);
}
import { useEffect } from 'react';

/**
 * Hook for setting body cursors.
 * Credits: https://github.com/pmndrs/drei#usecursor
 */
export function useCursor(
  hovered: boolean,
  onPointerOver = 'pointer',
  onPointerOut = 'auto'
) {
  useEffect(() => {
    if (hovered) {
      document.body.style.cursor = onPointerOver;
      return () => void (document.body.style.cursor = onPointerOut);
    }
  }, [hovered]);
}
