import React, { Fragment, FC, ReactElement } from 'react';
import { GradientProps, Gradient } from '../../Gradient';
import { CloneElement, useId } from 'reablocks';

export interface LinearAxisLineProps {
  height: number;
  width: number;
  strokeColor?: string;
  strokeWidth: number;
  strokeGradient: ReactElement<GradientProps, typeof Gradient> | null;
  scale: any;
  orientation: 'horizontal' | 'vertical';
  className?: string;
}

export const LinearAxisLine: FC<Partial<LinearAxisLineProps>> = ({
  strokeColor,
  strokeWidth,
  strokeGradient,
  scale,
  orientation,
  className
}) => {
  const id = useId();
  const [range0, range1] = scale.range();

  return (
    <Fragment>
      <line
        className={className}
        x1={orientation === 'vertical' ? 0 : range0}
        // Workaround for a Chrome/Firefox bug where it won't render gradients for straight lines
        x2={orientation === 'vertical' ? 0.00001 : range1}
        y1={orientation === 'vertical' ? range0 : 0}
        y2={orientation === 'vertical' ? range1 : 0.00001}
        strokeWidth={strokeWidth}
        stroke={strokeGradient ? `url(#axis-gradient-${id})` : strokeColor}
      />
      {strokeGradient && (
        <CloneElement<GradientProps>
          element={strokeGradient}
          id={`axis-gradient-${id}`}
        />
      )}
    </Fragment>
  );
};

LinearAxisLine.defaultProps = {
  strokeColor: '#8F979F',
  strokeWidth: 1
};
import { CloneElement } from 'reablocks';
import React, {
  FC,
  ReactElement,
  createRef,
  useCallback,
  useEffect,
  useState
} from 'react';
import { ChartDataTypes } from '../../data';
import { LinearAxisLine, LinearAxisLineProps } from './LinearAxisLine';
import {
  LinearAxisTickSeries,
  LinearAxisTickSeriesProps
} from './LinearAxisTickSeries';

export interface LinearAxisDimensionChanged {
  height?: number;
  width?: number;
}

export interface LinearAxisProps {
  height?: number;
  width?: number;
  domain?: ChartDataTypes[];
  scaled?: boolean;
  roundDomains?: boolean;
  type?: 'value' | 'time' | 'category' | 'duration';
  position?: 'start' | 'end' | 'center';
  tickSeries?: ReactElement<
    LinearAxisTickSeriesProps,
    typeof LinearAxisTickSeries
  >;
  axisLine?: ReactElement<LinearAxisLineProps, typeof LinearAxisLine> | null;
  scale?: any;
  visibility?: 'visible' | 'hidden';
  orientation?: 'horizontal' | 'vertical';
  onDimensionsChange?: (event: LinearAxisDimensionChanged) => void;
}

interface LinearAxisState {
  height?: number;
  width?: number;
}

export const LinearAxis: FC<Partial<LinearAxisProps>> = (props) => {
  const {
    position,
    tickSeries,
    axisLine,
    height,
    width,
    scale,
    orientation,
    visibility = 'visible',
    onDimensionsChange
  } = props;

  const containerRef = createRef<SVGGElement>();
  const [dimensions, setDimensions] = useState<LinearAxisState>({
    height: height,
    width: width
  });

  const updateDimensions = useCallback(() => {
    const shouldOffset = position !== 'center';

    let height;
    let width;
    if (shouldOffset) {
      const dims = containerRef.current!.getBoundingClientRect();
      width = Math.floor(dims.width);
      height = Math.floor(dims.height);
    }

    if (orientation === 'vertical') {
      if (dimensions.width !== width) {
        setDimensions({ ...dimensions, width: width });
        onDimensionsChange({ width });
      }
    } else {
      if (dimensions.height !== height) {
        setDimensions({ ...dimensions, height: height });
        onDimensionsChange({ height });
      }
    }
  }, [containerRef, dimensions, onDimensionsChange, orientation, position]);

  useEffect(() => {
    updateDimensions();
  }, [updateDimensions, height, width, scale]);

  function getPosition() {
    let translateY = 0;
    let translateX = 0;

    if (position === 'end' && orientation === 'horizontal') {
      translateY = height;
    } else if (position === 'center' && orientation === 'horizontal') {
      translateY = height / 2;
    } else if (position === 'end' && orientation === 'vertical') {
      translateX = width;
    } else if (position === 'center' && orientation === 'vertical') {
      translateX = width / 2;
    }

    return { translateX, translateY };
  }

  const { translateX, translateY } = getPosition();

  return (
    <g
      transform={`translate(${translateX}, ${translateY})`}
      ref={containerRef}
      visibility={visibility}
    >
      {axisLine && (
        <CloneElement<LinearAxisLineProps>
          element={axisLine}
          height={height}
          width={width}
          scale={scale}
          orientation={orientation}
        />
      )}
      {(tickSeries.props.line || tickSeries.props.label) && (
        <CloneElement<LinearAxisTickSeriesProps>
          element={tickSeries}
          height={height}
          width={width}
          scale={scale}
          orientation={orientation}
          axis={props}
        />
      )}
    </g>
  );
};

LinearAxis.defaultProps = {
  scaled: false,
  roundDomains: false,
  axisLine: <LinearAxisLine />,
  onDimensionsChange: () => undefined
};
import React, { FC, ReactElement } from 'react';
import {
  LinearAxisTickLine,
  LinearAxisTickLineProps
} from './LinearAxisTickLine';

export interface LinearAxisTickLabelProps {
  text: string;
  fullText: string;
  angle: number;
  orientation: 'horizontal' | 'vertical';
  half: 'start' | 'end' | 'center';
  line: ReactElement<LinearAxisTickLineProps, typeof LinearAxisTickLine>;
  format?: (v) => any;
  /**
   * Format tooltip title on hover label.
   */
  formatTooltip?: (value: any) => any | string;
  fill: string;
  fontSize: number;
  fontFamily: string;
  rotation: boolean | number;
  padding: number | { fromAxis: number; alongAxis: number };
  textAnchor?: 'start' | 'end' | 'middle';
  position: 'start' | 'end' | 'center';
  align: 'start' | 'end' | 'center' | 'inside' | 'outside';
  className?: string;
}

export const LinearAxisTickLabel: FC<Partial<LinearAxisTickLabelProps>> = ({
  text,
  fullText,
  angle,
  orientation,
  half,
  line,
  textAnchor,
  position,
  className,
  fill,
  fontSize,
  fontFamily,
  rotation,
  padding,
  formatTooltip,
  align
}) => {
  function getAlign() {
    if ((align === 'inside' || align === 'outside') && half === 'center') {
      return 'center';
    }

    if (align === 'inside') {
      return half === 'start' ? 'end' : 'start';
    }

    if (align === 'outside') {
      return half === 'start' ? 'start' : 'end';
    }

    return align;
  }

  // bug in this function - spacing is NA
  function getTickLineSpacing() {
    if (!line) {
      return [0, 0];
    }

    const size = line.props.size ?? 3;
    const position = line.props.position ?? 'center';

    if (position === 'start') {
      return [size * -1, 0];
    } else if (position === 'end') {
      return [0, size];
    } else {
      return [size * -0.5, size * 0.5];
    }
  }

  function getOffset() {
    const adjustedPadding =
      typeof padding === 'number'
        ? { fromAxis: padding, alongAxis: padding }
        : padding;

    const spacing = getTickLineSpacing();
    const offset1 =
      position === 'start'
        ? spacing[0] - adjustedPadding.fromAxis
        : position === 'end'
          ? spacing[1] + adjustedPadding.fromAxis
          : 0;

    const align = getAlign();
    let offset2 = 0;
    offset2 +=
      align === 'center'
        ? 0
        : align === 'start'
          ? -adjustedPadding.alongAxis
          : adjustedPadding.alongAxis;

    const horz = orientation === 'horizontal';

    return {
      [horz ? 'x' : 'y']: offset2,
      [horz ? 'y' : 'x']: offset1
    };
  }

  function getTextPosition() {
    let transform = '';
    let newtextAnchor = '';
    let alignmentBaseline = 'middle' as 'middle' | 'baseline' | 'hanging';

    if (angle !== 0) {
      transform = `rotate(${angle})`;
      newtextAnchor = 'end';
    } else {
      const align = getAlign();
      if (orientation === 'horizontal') {
        newtextAnchor =
          align === 'center' ? 'middle' : align === 'start' ? 'end' : 'start';
        if (position === 'start') {
          alignmentBaseline = 'baseline';
        } else if (position === 'end') {
          alignmentBaseline = 'hanging';
        }
      } else {
        alignmentBaseline =
          align === 'center'
            ? 'middle'
            : align === 'start'
              ? 'baseline'
              : 'hanging';
        if (position === 'start') {
          newtextAnchor = 'end';
        } else if (position === 'end') {
          newtextAnchor = 'start';
        } else {
          newtextAnchor = 'middle';
        }
      }
    }

    return {
      transform,
      textAnchor: textAnchor || newtextAnchor,
      alignmentBaseline
    };
  }

  const { x, y } = getOffset();
  const textPosition = getTextPosition();
  const titleHover =
    typeof formatTooltip === 'function' ? formatTooltip(fullText) : fullText;

  return (
    <g
      transform={`translate(${x}, ${y})`}
      fontSize={fontSize}
      fontFamily={fontFamily}
    >
      <title>{titleHover}</title>
      <text {...textPosition} fill={fill} className={className}>
        {text}
      </text>
    </g>
  );
};

