// https://stackoverflow.com/questions/673905/best-way-to-determine-users-locale-within-browser
const getNavigatorLanguage = () => {
  if (typeof window === 'undefined') {
    return 'en';
  }

  if (navigator.languages && navigator.languages.length) {
    return navigator.languages[0];
  }

  if (
    (navigator as any).userLanguage ||
    navigator.language ||
    (navigator as any).browserLanguage
  ) {
    return 'en';
  }
};

const locale = getNavigatorLanguage();

const options = {
  year: 'numeric',
  month: 'numeric',
  day: 'numeric',
  hour12: true,
  formatMatcher: 'best fit'
};

/**
 * Format a value based on type.
 */
export function formatValue(value: ChartInternalDataTypes): string {
  if (value !== undefined) {
    if (value instanceof Date) {
      return (value as Date).toLocaleDateString(locale, options as any);
    } else if (typeof value === 'number') {
      return value.toLocaleString();
    }

    return value as string;
  }

  return 'No value';
}

/**
 * Generate aria label text for the given data point(s)
 * @param datapoint 
 * @returns Aria Label
 */
export function getAriaLabel(datapoint) {
  const isArray = Array.isArray(datapoint);
  if (isArray) {
    return datapoint?.map(row => (getAriaLabel(row))).join(', ');
  } else {
    const key = datapoint?.key || datapoint?.x;
    // 'data' or 'y' will not be an array as the label is unique for each element
    const value = datapoint?.data || datapoint?.y;
    return `${key}: ${formatValue(value)}`;
  }
}
import { TimeInterval } from 'd3-time';

const ONE_DAY = 60 * 60 * 24;
const DURATION_TICK_STEPS = [
  0.001, // 1 ms
  0.005, // 5 ms
  0.01, // 10 ms
  0.05, // 50 ms
  0.1, // 100 ms
  0.5, // 500 ms
  1, // 1 s
  5, // 5 s
  10, // 10 s
  15, // 15 s
  60, // 1 m
  60 * 15, // 15 m
  60 * 30, // 30 m
  60 * 60, // 1 h
  60 * 60 * 2, // 2 h
  60 * 60 * 4, // 4 h
  60 * 60 * 6, // 6 h
  60 * 60 * 8, // 8 h
  60 * 60 * 12, // 12 h
  ONE_DAY // 24 h
];

/**
 * Reduce the ticks to the max number of ticks.
 */
export function reduceTicks<T>(ticks: T[], maxTicks: number) {
  if (ticks.length > maxTicks) {
    const reduced: T[] = [];
    const modulus = Math.floor(ticks.length / maxTicks);

    for (let i = 0; i < ticks.length; i++) {
      if (i % modulus === 0) {
        reduced.push(ticks[i]);
      }
    }
    ticks = reduced;
  }

  return ticks;
}

/**
 * Determine the max ticks for the available width.
 */
export function getMaxTicks(size: number, dimension: number) {
  const tickWidth = Math.max(size, 0);
  return Math.floor(dimension / tickWidth);
}

/**
 * Formats the ticks in a duration format.
 */
export function getDurationTicks(domain, maxTicks) {
  const domainWidth = domain[1] - domain[0];
  let tickStep: number | null = null;
  for (const s of DURATION_TICK_STEPS) {
    if (domainWidth / s < maxTicks) {
      tickStep = s;
      break;
    }
  }

  if (tickStep === null) {
    const numDayTicks = domainWidth / ONE_DAY;
    const dayStep = Math.ceil(numDayTicks / maxTicks);
    tickStep = ONE_DAY * dayStep;
  }

  const ticks = [domain[0]];
  while (ticks[ticks.length - 1] + tickStep <= domain[1]) {
    ticks.push(ticks[ticks.length - 1] + tickStep);
  }

  return ticks;
}

/**
 * Get the tick values from the scale.
 */
export function getTicks(
  scale: any,
  tickValues: any[],
  type: 'value' | 'category' | 'time' | 'duration',
  maxTicks = 100,
  interval?: number | TimeInterval
) {
  let result;

  if (tickValues) {
    result = tickValues;
  } else {
    if (scale.ticks) {
      if (type === 'duration') {
        result = getDurationTicks(scale.domain(), maxTicks);
      } else if (interval) {
        result = scale.ticks(interval);
      } else {
        if (type === 'time') {
          // If its time, we need to handle the time count
          // manually because d3 does this odd rounding
          result = scale.ticks();
          result = reduceTicks(result, maxTicks);
        } else {
          result = scale.ticks(maxTicks);
        }
      }
    } else {
      tickValues = scale.domain();
      result = reduceTicks(tickValues, maxTicks);
    }
  }

  return result;
}
export interface TextDimensions {
  height: number;
  width: number;
}

const cache: { [key: string]: TextDimensions } = {};

export const calculateDimensions = (
  text: string,
  fontFamily: string,
  fontSize: string | number
): TextDimensions => {
  const key = `${text}_${fontFamily}_${fontSize}`;

  // Check if we have a cache hit
  if (cache[key]) {
    return cache[key];
  }

  // If we are in a Node.js environment
  if (typeof window === 'undefined' || typeof document === 'undefined') {
    const height = parseInt(typeof fontSize === 'string' ? fontSize : fontSize.toString(), 10);
    const dimensions = {
      height,
      // 8 is an approximation of the width of a character
      width: text.length * 8
    };

    cache[key] = dimensions;

    return dimensions;
  }

  // Create a temporary div element
  const element = document.createElement('div');

  // Set up the style so the size can be measured
  element.style.fontFamily = fontFamily;
  element.style.fontSize = typeof fontSize === 'string' ? fontSize : `${fontSize}px`;
  element.style.position = 'absolute';
  element.style.left = '-9999px';
  element.style.whiteSpace = 'nowrap';
  element.style.height = 'auto';
  element.style.fontWeight = 'normal';
  element.style.lineHeight = 'normal';
  element.style.width = 'auto';
  element.style.wordBreak = 'normal';

  // Add the text to the div
  element.textContent = text;

  // Add the div to the body
  document.body.appendChild(element);

  // Measure the div
  const dimensions = {
    height: element.offsetHeight,
    width: element.offsetWidth
  };

  // Remove the div from the body
  document.body.removeChild(element);

  // Store the result in the cache for future calls
  cache[key] = dimensions;

  return dimensions;
};
import React, { FC, Fragment, ReactElement, useCallback, useMemo } from 'react';
import {
  LinearAxisTickLabel,
  LinearAxisTickLabelProps
} from './LinearAxisTickLabel';
import {
  LinearAxisTickLine,
  LinearAxisTickLineProps
} from './LinearAxisTickLine';
import { formatValue } from '../../utils/formatting';
import { getTicks, getMaxTicks } from '../../utils/ticks';
import { TimeInterval } from 'd3-time';
import { CloneElement } from 'reablocks';
import { LinearAxisProps } from './LinearAxis';
import ellipsize from 'ellipsize';
import { max } from 'd3-array';
import { calculateDimensions } from '../../utils/size';

export interface LinearAxisTickSeriesProps {
  height: number;
  width: number;
  scale: any;
  interval?: number | TimeInterval;
  tickSize: number;
  tickValues: any[];
  orientation: 'horizontal' | 'vertical';
  label: ReactElement<
    LinearAxisTickLabelProps,
    typeof LinearAxisTickLabel
  > | null;
  line: ReactElement<LinearAxisTickLineProps, typeof LinearAxisTickLine> | null;
  axis: LinearAxisProps;
}

interface ProcessedTick {
  text: string;
  fullText: string;
  x: number;
  y: number;
  height: number;
  width: number;
  half: 'start' | 'end' | 'center';
}

export const LinearAxisTickSeries: FC<Partial<LinearAxisTickSeriesProps>> = ({
  scale,
  orientation,
  height,
  width,
  label,
  tickSize,
  tickValues,
  interval,
  line,
  axis
}) => {
  /**
   * Gets the adjusted scale given offsets.
   */
  const getAdjustedScale = useCallback(() => {
    if (scale.bandwidth) {
      let offset = scale.bandwidth() / 2;
      if (scale.round()) {
        offset = Math.round(offset);
      }

      return (d) => +scale(d) + offset;
    } else {
      return (d) => +scale(d);
    }
  }, [scale]);

  /**
   * Gets the x/y position for a given tick.
   */
  const getPosition = useCallback(
    (scaledTick: number) => {
      if (orientation === 'horizontal') {
        return { x: scaledTick, y: 0 };
      } else {
        return { x: 0, y: scaledTick };
      }
    },
    [orientation]
  );

  /**
   * Gets the dimension (height/width) this axis is calculating on.
   */
  const getDimension = useCallback(() => {
    return orientation === 'vertical' ? height : width;
  }, [height, orientation, width]);

  /**
   * Gets the formatted label of the tick.
   */
  const labelFormatFn = useMemo((): any => {
    if (label && label.props.format) {
      return label.props.format;
    } else if (scale.tickFormat) {
      return scale.tickFormat.apply(scale, [5]);
    } else {
      return (v) => formatValue(v);
    }
  }, [label, scale]);

  /**
   * Gets the ticks given the dimensions and scales and returns
   * the text and position.
   */
  const ticks = useMemo((): ProcessedTick[] => {
    const dimension = getDimension();
    const maxTicks = getMaxTicks(tickSize, dimension);
    const ticks = getTicks(scale, tickValues, axis.type, maxTicks, interval);
    const adjustedScale = getAdjustedScale();
    const format = labelFormatFn;
    const midpoint = dimension / 2;

    return ticks.map((tick) => {
      const fullText = format(tick);
      const scaledTick = adjustedScale(tick);
      const position = getPosition(scaledTick);
      const text = ellipsize(fullText, 18);
      const size = label
        ? calculateDimensions(
          text,
          label.props.fontFamily,
          label.props.fontSize.toString()
        )
        : {};

      return {
        ...position,
        ...size,
        text,
        fullText,
        half:
          scaledTick === midpoint
            ? 'center'
            : scaledTick < midpoint
              ? 'start'
              : 'end'
      };
    });
  }, [
    axis.type,
    getAdjustedScale,
    getDimension,
    getPosition,
    interval,
    label,
    labelFormatFn,
    scale,
    tickSize,
    tickValues
  ]);

  /**
   * Calculates the rotation angle that the ticks need to be shifted to.
   * This equation will measure the length of the text in a external canvas
   * object and determine what the longest label is and rotate until they fit.
   */
  const angle = useMemo((): number => {
    if (!label) {
      return 0;
    }

    const labelProps = label.props;
    const dimension = getDimension();
    const maxTicksLength = max(ticks, (tick) => tick.width);
    let angle = 0;

    if (labelProps.rotation) {
      if (labelProps.rotation === true) {
        let baseWidth = maxTicksLength;
        const maxBaseWidth = Math.floor(dimension / ticks.length);

        while (baseWidth > maxBaseWidth && angle > -90) {
          angle -= 30;
          baseWidth = Math.cos(angle * (Math.PI / 180)) * maxTicksLength;
        }
      } else {
        angle = labelProps.rotation;
      }
    }

    return angle;
  }, [getDimension, label, ticks]);

  return (
    <Fragment>
      {ticks.map((tick, i) => (
        <g key={i} transform={`translate(${tick.x}, ${tick.y})`}>
          {line && (
            <CloneElement<LinearAxisTickLineProps>
              element={line}
              height={height}
              width={width}
              orientation={orientation}
            />
          )}
          {label && (
            <CloneElement<LinearAxisTickLabelProps>
              element={label}
              text={tick.text}
              fullText={tick.fullText}
              half={tick.half}
              angle={angle}
              orientation={orientation}
              line={line!}
            />
          )}
        </g>
      ))}
    </Fragment>
  );
};

