export const bigIntegerToLocaleString = (bigInteger) => {
  let i = 0;
  let formattedString = '';
  for (const c of bigInteger.toString().split('').reverse()) {
    if (i > 0 && i % 3 === 0) {
      formattedString = ',' + formattedString;
    }
    formattedString = c + formattedString;
    i++;
  }
  return formattedString;
};
