export const getPositionForTarget = ({ target, clientX, clientY }) => {
  const rect = target.getBoundingClientRect();
  return {
    x: clientX - (rect?.left || 0) - target.clientLeft,
    y: clientY - (rect?.top || 0) - target.clientTop
