   * Width of the chart. If not provided will autosize.
   */
  width?: number;

  /**
   * Height of the chart. If not provided will autosize.
   */
  height?: number;

  /**
   * Margins for the chart.
   */
  margins?: Margins;

  /**
   * Classnames for the chart.
   */
  className?: string;

  /**
   * Classnames for the chart.
   */
  containerClassName?: string;

  /**
   * Additional css styles.
   */
  style?: React.StyleHTMLAttributes<SVGSVGElement>;

  /**
   * Center the chart. Used mainly internally.
   */
  center?: boolean;

  /**
   * Center chart on X Axis only. Used mainly internally.
   */
  centerX?: boolean;

  /**
   * Center chart on Y Axis only. Used mainly internally.
   */
  centerY?: boolean;
}

export interface ChartContainerProps extends ChartProps {
  /**
   * Internal property to identify if the xAxis is visible.
   */
  xAxisVisible?: boolean;

  /**
   * Internal property to identify if the xAxis is visible.
   */
  yAxisVisible?: boolean;

  /**
   * Children elements to recieve the calculated props.
   */
  children: (props: ChartContainerChildProps) => any;
}

export type ChartContainerChildProps = ChartContextProps;

export const ChartContainer: FC<ChartContainerProps> = ({
  className,
  children,
  center,
  centerX,
  centerY,
  style,
  margins,
  containerClassName,
  xAxisVisible,
  yAxisVisible,
  id,
  ...rest
}) => {
  const curId = useId(id);
  const [xAxisSized, setXAxisSized] = useState<boolean>(false);
  const [yAxisSized, setYAxisSized] = useState<boolean>(false);
  const [xOffset, setXOffset] = useState<number>(0);
  const [yOffset, setYOffset] = useState<number>(0);
  const { observe, width, height } = useDimensions<HTMLDivElement>();

  const chartSized = useMemo(() => {
    if (!height || !width) {
      return false;
    }

    // TODO: @amcdnl refactor this to account for 0-2 axises on x/y
    if (xAxisVisible && !xAxisSized) {
      return false;
    }

    if (yAxisVisible && !yAxisSized) {
      return false;
    }

    return true;
  }, [height, width, xAxisSized, xAxisVisible, yAxisVisible, yAxisSized]);

  const onUpdateAxes = useCallback(
    (
      orientation: 'horizontal' | 'vertical',
      event: LinearAxisDimensionChanged
    ) => {
      if (orientation === 'horizontal') {
        setXAxisSized(true);
      } else {
        setYAxisSized(true);
      }

      if (event.height) {
        setYOffset(event.height);
      }

      if (event.width) {
        setXOffset(event.width);
      }
    },
    []
  );

  const childProps: ChartContainerChildProps = useMemo(
    () => ({
      chartSized,
      id: curId,
      updateAxes: onUpdateAxes,
      yAxisSized,
      xAxisSized,
      ...getDimension({
        margins,
        height,
        width,
        yOffset,
        xOffset
      })
    }),
    [
      chartSized,
      curId,
      onUpdateAxes,
      yAxisSized,
      xAxisSized,
      margins,
      height,
      width,
      yOffset,
      xOffset
    ]
  );

  const translateX = center || centerX ? width / 2 : childProps.xMargin;
  const translateY = center || centerY ? height / 2 : childProps.yMargin;

  const styleHeight =
    rest.height !== undefined && rest.height !== null ? rest.height : '100%';
  const styleWidth =
    rest.width !== undefined && rest.width !== null ? rest.width : '100%';

  return (
    <div
      ref={observe}
      style={{ height: styleHeight, width: styleWidth }}
      className={classNames(containerClassName, css.container)}
      {...rest}
    >
      <ChartProvider value={childProps}>
        {height > 0 && width > 0 && (
          <svg
            width={width}
            height={height}
            className={classNames(css.svg, className)}
            style={style}
            tabIndex={0}
          >
            <g transform={`translate(${translateX}, ${translateY})`}>
              {children(childProps)}
            </g>
          </svg>
        )}
      </ChartProvider>
    </div>
  );
};
import bigInt from 'big-integer';
import { bigIntegerToLocaleString } from '../../common/utils/bigint';
import {
  ChartDataTypes,
  ChartInternalDataTypes,
  ChartNestedDataShape,
  ChartShallowDataShape
} from './types';

export function normalizeValue(
  value: ChartDataTypes,
  maxBigInt: bigInt.BigInteger
): ChartInternalDataTypes {
  if (bigInt.isInstance(value)) {
    if (maxBigInt.greater(1000000)) {
      const divideBy = maxBigInt.divide(1000000);
      return (value as bigInt.BigInteger).divide(divideBy).toJSNumber();
    } else {
      return (value as bigInt.BigInteger).toJSNumber();
    }
  } else {
    return value as ChartInternalDataTypes;
  }
}

export function normalizeValueForFormatting(
  value: ChartDataTypes
): ChartInternalDataTypes {
  if (bigInt.isInstance(value)) {
    return bigIntegerToLocaleString(value as bigInt.BigInteger);
  }
  return value as ChartInternalDataTypes;
}

export function getMaxBigIntegerForNested(series: ChartNestedDataShape[]) {
  let maxBigInteger = bigInt.one;
  for (const group of series) {
    const maxBigIntegerForGroup = getMaxBigIntegerForShallow(group.data);
    if (maxBigIntegerForGroup.greater(maxBigInteger)) {
      maxBigInteger = maxBigIntegerForGroup;
    }
  }
  return maxBigInteger;
}

export function getMaxBigIntegerForShallow(series: ChartShallowDataShape[]) {
  let maxBigInteger = bigInt.one;
  for (const point of series) {
    if (bigInt.isInstance(point.data)) {
      const bigInteger = point.data as bigInt.BigInteger;
      if (bigInteger.greater(maxBigInteger)) {
        maxBigInteger = bigInteger;
      }
    }
  }
  return maxBigInteger;
}
import { median } from 'd3-array';
import {
  ChartInternalNestedDataShape,
  ChartShallowDataShape,
  ChartNestedDataShape,
  ChartInternalShallowDataShape,
  ChartDataTypes
} from './types';
import {
  getMaxBigIntegerForNested,
  getMaxBigIntegerForShallow,
  normalizeValue,
  normalizeValueForFormatting
} from './bigInteger';
import bigInt from 'big-integer';

export type Direction = 'vertical' | 'horizontal';

/**
 * Accepts a `ChartDataShape` and transforms it to a chart readable data shape.
 *
 * Example:
 *
 *   [{
 *    key: 'Threat Intel',
 *    data: [{ key:'2011', data: 25 }]
 *   }]
 *
 * will be transformed to:
 *
 *  [{
 *    key: 'Threat Intel',
 *    data: [
 *      key: 'Threat Intel',
 *      x: '2011',
 *      y: 25
 *    ]
 *  }]
 */
export function buildNestedChartData(
  series: ChartNestedDataShape[],
  sort = false,
  direction: Direction = 'vertical'
): ChartInternalNestedDataShape[] {
  let result: ChartInternalNestedDataShape[] = [];
  const maxBigInteger = getMaxBigIntegerForNested(series);
  const isVertical = direction === 'vertical';

  for (const point of series) {
    for (const nestedPoint of point.data) {
      const key = normalizeValueForFormatting(point.key);
      let idx = result.findIndex((r) => {
        const left = r.key;
        if (left instanceof Date && key instanceof Date) {
          return left.getTime() === key.getTime();
        }
        return left === key;
      });

      if (idx === -1) {
        result.push({
          key,
          metadata: point.metadata,
          data: []
        });

        idx = result.length - 1;
      }

      const x = normalizeValue(
        isVertical ? nestedPoint.key : nestedPoint.data,
        maxBigInteger
      );

      const y = normalizeValue(
        isVertical ? nestedPoint.data : nestedPoint.key,
        maxBigInteger
      );

      result[idx].data.push({
        key,
        value: normalizeValueForFormatting(nestedPoint.data),
        metadata: nestedPoint.metadata,
        id: point.id,
        x,
        x0: isVertical ? x : 0,
        x1: x,
        y,
        y0: isVertical ? 0 : y,
        y1: y
      });
    }
  }

  // Sort the series data based on the median value
  if (sort) {
    result = result.sort((a, b) => {
      const aMax = median(a.data, (d: any) => d.y)!;
      const bMax = median(b.data, (d: any) => d.y)!;
      return aMax < bMax ? 1 : -1;
    });
  }

  return result;
}

function addToChartType(
  a: ChartDataTypes,
  b: number | bigInt.BigInteger
): ChartDataTypes {
  if (bigInt.isInstance(a) && bigInt.isInstance(b)) {
    return (a as bigInt.BigInteger).add(b as bigInt.BigInteger);
  } else if (a instanceof Date && typeof b === 'number') {
    return new Date(a.valueOf() + b);
  } else if (typeof a === 'number' && typeof b === 'number') {
    return a + b;
  } else {
    throw new Error('Invalid types to addToChartTypes');
  }
}

/**
 * Accepts a shallow shape and normalizes it to a chart readable format.
 */
export function buildShallowChartData(
  series: ChartShallowDataShape[],
  direction: Direction = 'vertical',
  binSize: number | undefined = undefined
): ChartInternalShallowDataShape[] {
  const result: ChartInternalShallowDataShape[] = [];
  const maxBigInteger = getMaxBigIntegerForShallow(series);
  const isVertical = direction === 'vertical';

  for (const point of series) {
    const isTuple = Array.isArray(point.data);
    let k1 = point.key;
    if (binSize) {
      k1 = addToChartType(point.key, binSize);
    }

    const props = {
      k0: normalizeValue(point.key, maxBigInteger),
      k1: normalizeValue(k1, maxBigInteger),
      v0: normalizeValue(isTuple ? point.data[0] : 0, maxBigInteger),
      v1: normalizeValue(isTuple ? point.data[1] : point.data, maxBigInteger)
    };

    const xProp = isVertical ? 'k' : 'v';
    const yProp = isVertical ? 'v' : 'k';

    result.push({
      key: normalizeValueForFormatting(props.k0),
      value: normalizeValueForFormatting(props.v1),
      metadata: point.metadata,
      id: point.id,
      x: props[`${xProp}1`],
      x0: props[`${xProp}0`],
      x1: props[`${xProp}1`],
      y: props[`${yProp}1`],
      y0: props[`${yProp}0`],
      y1: props[`${yProp}1`]
    });
  }

  return result;
}
import { histogram } from 'd3-array';
import { ChartInternalShallowDataShape, ChartInternalDataTypes } from './types';

/**
 * Build a histogram given data set.
 */
export function buildBins(
  xScale,
  thresholds,
  data: any[]
): ChartInternalShallowDataShape[] {
  const layout = histogram()
    .value((d: any) => d.x)
    .domain(xScale.domain())
    .thresholds(xScale.ticks(thresholds));

  const bins = layout(data as any);

  return bins.map((bin) => ({
    x0: bin.x0 as ChartInternalDataTypes,
    x1: bin.x1 as ChartInternalDataTypes,
    y: bin.length,
    y0: 0,
    y1: bin.length
  }));
}
import { stack, stackOffsetExpand, stackOffsetDiverging } from 'd3-shape';
import { ChartNestedDataShape, ChartInternalNestedDataShape } from './types';
import {
  getMaxBigIntegerForNested,
  normalizeValue,
  normalizeValueForFormatting
} from './bigInteger';
import { uniqueBy } from '../../common/utils/array';

export type StackTypes = 'default' | 'expand' | 'diverging';

/**
 * Given a dataset like:
 *
 *   [{
 *    key: 'Threat Intel',
 *    data: [{ key:'2011', data: 25 }]
 *   }]
 *
 * it will transform it to:
 *
 *  [
 *    { x: 'Theat Intel', '2011': 25 }
 *  ]
 */
function transformDataToStack(data: ChartNestedDataShape[]) {
  const result: any[] = [];
  const maxBigInteger = getMaxBigIntegerForNested(data);

  for (const category of data) {
    for (const value of category.data) {
      let idx = result.findIndex((r) => {
        if (r.x instanceof Date && category.key instanceof Date) {
          return r.x.getTime() === category.key.getTime();
        }
        return r.x === category.key;
      });

      if (idx === -1) {
        result.push({
          metadata: category.metadata,
          x: category.key,
          formattedValues: {}
        });

        idx = result.length - 1;
      }

      result[idx].metadata = value.metadata;

      result[idx][value.key as string] = normalizeValue(
        value.data,
        maxBigInteger
      );

      result[idx].formattedValues[value.key as string] =
        normalizeValueForFormatting(value.data);
    }
  }

  return result;
}

/**
 * Translates the stack data to a chart standard dataset.
 */
function transformStackToData(
  stackData,
  direction = 'vertical'
): ChartInternalNestedDataShape[] {
  const result: ChartInternalNestedDataShape[] = [];
  const isVertical = direction === 'vertical';

  // Transform the data from the d3 stack format to our internal format
  for (const category of stackData) {
    for (const point of category) {
      const key = point.data.x;

      let idx = result.findIndex((r) => {
        if (r.key instanceof Date && key instanceof Date) {
          return r.key.getTime() === key.getTime();
        }
        return r.key === key;
      });

      if (idx === -1) {
        result.push({
          key,
          data: []
        });

        idx = result.length - 1;
      }

      const categoryKey = category.key;
      const y = point.data[categoryKey];
      const [y0, y1] = point;

      result[idx].data.push({
        metadata: point.data.metadata,
        key,
        x: isVertical ? categoryKey : y1,
        x0: isVertical ? categoryKey : y0,
        x1: isVertical ? categoryKey : y1,
        y: isVertical ? y : categoryKey,
        y0: isVertical ? y0 : categoryKey,
        y1: isVertical ? y1 : categoryKey,
        value: point.data.formattedValues[categoryKey]
      });
    }
  }

  return result;
}

/**
 * Builds a stack dataset from the standard data format.
 */
export function buildBarStackData(
  data: ChartNestedDataShape[] = [],
  offset: StackTypes = 'default',
  direction = 'vertical'
) {
  const keys = uniqueBy<ChartNestedDataShape>(
    data,
    (d) => d.data,
    (d) => d.key
  );
  const stackData = transformDataToStack(data);

  let stackFn = stack();
  if (offset === 'expand') {
    stackFn = stackFn.offset(stackOffsetExpand);
  } else if (offset === 'diverging') {
    stackFn = stackFn.offset(stackOffsetDiverging);
  }

  const result = stackFn.keys(keys)(stackData);

  return transformStackToData(result, direction);
}
import { ChartNestedDataShape, ChartInternalNestedDataShape } from './types';
import { buildBarStackData } from './barStack';

/**
 * Builds a stack dataset from the standard data format.
 */
export function buildMarimekkoData(
  data: ChartNestedDataShape[] = []
): ChartInternalNestedDataShape[] {
  const result = buildBarStackData(data, 'expand');
  const sums = {};

  // Calculate the sum for each series and the total sum
  let totalSum = 0;
  for (const series of result) {
    const sum = series.data.reduce((acc, cur) => acc + (cur.y as number), 0);

    sums[series.key as string] = sum;
    totalSum += sum;
  }

  // Calculate the x0/x1 for each series
  let prev = 0;
  for (const series of result) {
    const x0 = prev;
    const x1 = prev + sums[series.key as string] / totalSum;
    prev = x1;

    for (const point of series.data) {
      point.x0 = x0;
      point.x1 = x1;
    }
  }

  return result;
}
import { stack, stackOffsetExpand } from 'd3-shape';
import {
