export const buildWaterfall = (
  series: ChartShallowDataShape[],
  direction: Direction = 'vertical',
  binSize: number | undefined = undefined
) => {
  const data = buildShallowChartData(series, direction, binSize);
  const isVertical = direction === 'vertical';
  const v = isVertical ? 'y' : 'x';

  let cumulative = 0;
  for (const point of data) {
    point[`${v}0`] = cumulative;
    cumulative += point[v] as number;
    point[`${v}1`] = cumulative;
    point[v] = cumulative;
  }

  return data;
};
import React, { Component, PropsWithChildren, createRef } from 'react';
import { toggleTextSelection } from '../utils/selection';
import { smoothMatrix, transform, translate } from 'transformation-matrix';
import { constrainMatrix } from '../utils/position';

interface PanProps extends PropsWithChildren {
  disabled: boolean;
  threshold: number;
  cursor?: string;
  x: number;
  y: number;
  scale: number;
  matrix: any;
  width: number;
  height: number;
  constrain: boolean;
  globalPanning: boolean;
  onPanStart: (event: PanStartEvent) => void;
  onPanMove: (event: PanMoveEvent) => void;
  onPanEnd: (event: PanEndEvent) => void;
  onPanCancel: (event: PanCancelEvent) => void;
}

export interface PanStartEvent {
  source: 'mouse' | 'touch';
  nativeEvent: MouseEvent | TouchEvent;
}

export interface PanMoveEvent {
  source: 'mouse' | 'touch';
  x: number;
  y: number;
  nativeEvent: MouseEvent | TouchEvent;
}

export interface PanEndEvent {
  source: 'mouse' | 'touch';
  nativeEvent: MouseEvent | TouchEvent;
}

export interface PanCancelEvent {
  source: 'mouse' | 'touch';
  nativeEvent: MouseEvent | TouchEvent;
}

export class Pan extends Component<PanProps> {
  static defaultProps: Partial<PanProps> = {
    x: 0,
    y: 0,
    disabled: false,
    scale: 1,
    threshold: 10,
    globalPanning: true,
    onPanStart: () => undefined,
    onPanMove: () => undefined,
    onPanEnd: () => undefined,
    onPanCancel: () => undefined
  };

  prevXPosition: number = 0;
  prevYPosition: number = 0;
  started: boolean = false;
  deltaX: number = 0;
  deltaY: number = 0;
  childRef = createRef<SVGGElement>();

  componentDidMount() {
    if (this.childRef.current) {
      this.childRef.current.addEventListener('mousedown', this.onMouseDown, {
        passive: false
      });
      this.childRef.current.addEventListener('touchstart', this.onTouchStart, {
        passive: false
      });
    }
  }

  componentWillUnmount() {
    this.disposeHandlers();

    if (this.childRef.current) {
      this.childRef.current.removeEventListener('mousedown', this.onMouseDown);
      this.childRef.current.removeEventListener(
        'touchstart',
        this.onTouchStart
      );
    }
  }

  disposeHandlers() {
    window.removeEventListener('mousemove', this.onMouseMove);
    window.removeEventListener('mouseup', this.onMouseUp);
    window.removeEventListener('touchmove', this.onTouchMove);
    window.removeEventListener('touchend', this.onTouchEnd);

    // Reset cursor on body back to original
    document.body.style['cursor'] = 'inherit';
    toggleTextSelection(true);
  }

  checkThreshold() {
    const { threshold } = this.props;
    return (
      !this.started &&
      (Math.abs(this.deltaX) > threshold || Math.abs(this.deltaY) > threshold)
    );
  }

  onPanStart(nativeEvent, source: 'mouse' | 'touch') {
    this.props.onPanStart({
      nativeEvent,
      source
    });
  }

  onPanMove(x: number, y: number, source: 'mouse' | 'touch', nativeEvent) {
    this.props.onPanMove({
      source,
      nativeEvent,
      x,
      y
    });
  }

  onPanEnd(nativeEvent, source: 'mouse' | 'touch') {
    const { onPanEnd } = this.props;

    onPanEnd({
      nativeEvent,
      source
    });
  }

  pan(x: number, y: number, nativeEvent, source: 'mouse' | 'touch') {
    const { scale, constrain, width, height, matrix } = this.props;

    const newMatrix = smoothMatrix(
      transform(matrix, translate(x / scale, y / scale)),
      100
    );

    const shouldConstrain =
      constrain && constrainMatrix(height, width, newMatrix);
    if (!shouldConstrain) {
      this.onPanMove(newMatrix.e, newMatrix.f, source, nativeEvent);
    }

    return shouldConstrain;
  }

  onMouseDown = (event: MouseEvent) => {
    // Stop at disabled
    if (this.props.disabled) {
      return;
    }

    // Ignore right click
    if (event.which === 3) {
      return;
    }

    // If global panning is turned off, it will only pan on the container
    if (
      !this.props.globalPanning &&
      event.target &&
      !(event.target as HTMLElement).classList.contains('pan-container')
    ) {
      return;
    }

    event.preventDefault();
    event.stopPropagation();

    toggleTextSelection(false);
    this.started = false;

    // Always bind event so we cancel movement even if no action was taken
    window.addEventListener('mousemove', this.onMouseMove);
    window.addEventListener('mouseup', this.onMouseUp);
  };

  onMouseMove = (event: MouseEvent) => {
    event.preventDefault();
    event.stopPropagation();

    this.deltaX = this.deltaX + event.movementX;
    this.deltaY = this.deltaY + event.movementY;

    if (this.checkThreshold()) {
      if (this.props.cursor) {
        document.body.style['cursor'] = this.props.cursor;
      }

      this.deltaX = 0;
      this.deltaY = 0;
      this.started = true;

      this.onPanStart(event, 'mouse');
    } else {
      this.pan(event.movementX, event.movementY, event, 'mouse');
    }
  };

  onMouseUp = (event: MouseEvent) => {
    event.preventDefault();
    event.stopPropagation();

    this.disposeHandlers();
    toggleTextSelection(true);

    if (this.started) {
      this.onPanEnd(event, 'mouse');
    } else {
      this.props.onPanCancel({
        nativeEvent: event,
        source: 'mouse'
      });
    }
  };

  onTouchStart = (event: TouchEvent) => {
    // Stop at disabled
    if (this.props.disabled) {
      return;
    }

    // Reqquire more than one touch
    if (event.touches.length !== 1) {
      return;
    }

    event.preventDefault();
    event.stopPropagation();

    toggleTextSelection(false);
    this.started = false;

    this.prevXPosition = event.touches[0].clientX;
    this.prevYPosition = event.touches[0].clientY;

    // Always bind event so we cancel movement even if no action was taken
    window.addEventListener('touchmove', this.onTouchMove);
    window.addEventListener('touchend', this.onTouchEnd);
  };

  onTouchMove = (event: TouchEvent) => {
    event.preventDefault();
    event.stopPropagation();

    // Calculate delta from previous position and current
    const x = event.touches[0].clientX;
    const y = event.touches[0].clientY;

    const deltaX = x - this.prevXPosition;
    const deltaY = y - this.prevYPosition;

    this.deltaX = this.deltaX + deltaX;
    this.deltaY = this.deltaY + deltaY;

    if (this.checkThreshold()) {
      this.deltaX = 0;
      this.deltaY = 0;
      this.started = true;

      this.onPanStart(event, 'touch');
    } else {
      const contrained = this.pan(deltaX, deltaY, event, 'touch');

      if (!contrained) {
        this.prevXPosition = x;
        this.prevYPosition = y;
      }
    }
  };

  onTouchEnd = (event: TouchEvent) => {
    event.preventDefault();
    event.stopPropagation();

    this.disposeHandlers();
    toggleTextSelection(true);

    if (this.started) {
      this.onPanEnd(event, 'touch');
    } else {
      this.props.onPanCancel({
        nativeEvent: event,
        source: 'touch'
      });
    }
  };

  render() {
    return <g ref={this.childRef}>{this.props.children}</g>;
  }
}
/**
 * Gets the position between a given set of points.
 */
export const getMidpoint = (pointA, pointB) => ({
  x: (pointA.x + pointB.x) / 2,
  y: (pointA.y + pointB.y) / 2
});

/**
 * Gets the distance between a given set of points.
 */
export const getDistanceBetweenPoints = (pointA, pointB) =>
  Math.sqrt(
    Math.pow(pointB.y - pointA.y, 2) + Math.pow(pointB.x - pointA.x, 2)
  );

/**
 * Get touch points.
 */
export function getTouchPoints(event, node) {
  const { left, top } = node.getBoundingClientRect();

  const [pointA, pointB] = [...event.touches].map((touch) => ({
    x: touch.clientX - Math.round(left),
    y: touch.clientY - Math.round(top)
  }));

  const distance = getDistanceBetweenPoints(pointA, pointB);
  const midpoint = getMidpoint(pointA, pointB);

  return {
    pointA,
    pointB,
    distance,
    midpoint
  };
}
import React, { Component, PropsWithChildren, createRef } from 'react';
import { toggleTextSelection } from '../utils/selection';
import {
  getPointFromMatrix,
  isZoomLevelGoingOutOfBounds
} from '../utils/position';
import { getTouchPoints } from './pinchUtils';
import {
  scale,
  smoothMatrix,
  transform,
  translate,
  applyToPoint,
  inverse
} from 'transformation-matrix';

interface ZoomGestureProps extends PropsWithChildren {
  disabled?: boolean;
  maxZoom: number;
  minZoom: number;
  scaleFactor: number;
  scale: number;
  matrix: any;
  x: number;
  y: number;
  style?: any;
  disableMouseWheel?: boolean;
  requireZoomModifier?: boolean;
  onZoom: (event: ZoomEvent) => void;
  onZoomEnd: () => void;
}

export interface ZoomEvent {
  scale: number;
  x: number;
  y: number;
  nativeEvent: any;
}

export class Zoom extends Component<ZoomGestureProps> {
  static defaultProps: Partial<ZoomGestureProps> = {
    x: 0,
    y: 0,
    scale: 1,
    scaleFactor: 0.1,
    minZoom: 1,
    maxZoom: 10
  };

  firstTouch: any;
  lastDistance: any;
  timeout: any;
  childRef = createRef<SVGGElement>();
  rqf: any;

  componentDidMount() {
    const { disabled, disableMouseWheel } = this.props;

    const ref = this.childRef.current;
    if (!disabled && ref) {
      if (!disableMouseWheel) {
        ref.addEventListener('mousewheel', this.onMouseWheel, {
          passive: false
        });
      }

      ref.addEventListener('touchstart', this.onTouchStart, { passive: false });
    }
  }

  componentWillUnmount() {
    window.removeEventListener('touchmove', this.onTouchMove);
    window.removeEventListener('touchend', this.onTouchEnd);
    cancelAnimationFrame(this.rqf);
    clearTimeout(this.timeout);

    const ref = this.childRef.current;
    if (ref) {
      ref.removeEventListener('mousewheel', this.onMouseWheel);
      ref.removeEventListener('touchstart', this.onTouchStart);
    }

    toggleTextSelection(true);
  }

  getStep(delta: number) {
    const { scaleFactor } = this.props;
    return -delta > 0 ? scaleFactor + 1 : 1 - scaleFactor;
  }

  scale(x: number, y: number, step: number, nativeEvent) {
    const { minZoom, maxZoom, onZoom, matrix } = this.props;

    const outside = isZoomLevelGoingOutOfBounds(
      {
        d: matrix.a,
        scaleFactorMin: minZoom,
        scaleFactorMax: maxZoom
      },
      step
    );

    if (!outside) {
      const newMatrix = smoothMatrix(
        transform(
          matrix,
          translate(x, y),
          scale(step, step),
          translate(-x, -y)
        ),
        100
      );

      this.rqf = requestAnimationFrame(() => {
        onZoom({
          scale: newMatrix.a,
          x: newMatrix.e,
          y: newMatrix.f,
          nativeEvent
        });
      });
    }

    return outside;
  }

  onMouseWheel = (event) => {
    const {
      disableMouseWheel,
      requireZoomModifier,
      matrix,
      onZoomEnd
    } = this.props;

    if (disableMouseWheel) {
      return false;
    }

    const hasModifier = event.metaKey || event.ctrlKey;
    if (requireZoomModifier && !hasModifier) {
      return false;
    }

    event.preventDefault();
    event.stopPropagation();

    const point = getPointFromMatrix(event, matrix);
    if (point) {
      const { x, y } = point as { x: number; y: number };
      const step = this.getStep(event.deltaY);

      this.scale(x, y, step, event);

      // Do small timeout to 'guess' when its done zooming
      clearTimeout(this.timeout);
      this.timeout = setTimeout(() => onZoomEnd(), 500);
    }
  };

  onTouchStart = (event: TouchEvent) => {
    if (event.touches.length === 2) {
      event.preventDefault();
      event.stopPropagation();
      toggleTextSelection(false);

      this.firstTouch = getTouchPoints(event, this.childRef.current);
      this.lastDistance = this.firstTouch.distance;

      window.addEventListener('touchmove', this.onTouchMove);
      window.addEventListener('touchend', this.onTouchEnd);
    }
  };

  onTouchMove = (event: TouchEvent) => {
    if (event.touches.length === 2) {
      event.preventDefault();
      event.stopPropagation();

      const { distance } = getTouchPoints(event, this.childRef.current);
      const distanceFactor = distance / this.lastDistance;

      const point = applyToPoint(inverse(this.props.matrix), {
        x: this.firstTouch.midpoint.x,
        y: this.firstTouch.midpoint.y
      }) as { x: number; y: number };

      if (point.x && point.y) {
        const outside = this.scale(point.x, point.y, distanceFactor, event);

        if (!outside) {
          this.lastDistance = distance;
        }
      }
    }
  };

  onTouchEnd = (event: TouchEvent) => {
    event.preventDefault();
    event.stopPropagation();

    window.removeEventListener('touchmove', this.onTouchMove);
    window.removeEventListener('touchend', this.onTouchEnd);

    toggleTextSelection(true);
    this.props.onZoomEnd();
  };

  render() {
    const { style, children } = this.props;
    return (
      <g ref={this.childRef} style={style}>
        {children}
      </g>
    );
  }
}
