import React, { Fragment, FC } from 'react';
import { formatValue } from '../utils/formatting';
import { ChartInternalDataTypes } from '../data';
import css from './TooltipTemplate.module.css';

interface SingleTooltipValue {
  key?: ChartInternalDataTypes;
  value?: ChartInternalDataTypes;
  x: ChartInternalDataTypes;
  y: ChartInternalDataTypes;
}

interface MultipleTooltipValues {
  x: ChartInternalDataTypes;
  data: SingleTooltipValue[];
}

interface TooltipTemplateProps {
  /**
   * Tooltip data value.
   */
  value?: SingleTooltipValue | MultipleTooltipValues;

  /**
   * Color scheme to apply.
   */
  color?: any;

  /**
   * Additional CSS classes to apply.
   */
  className?: any;
}

export const TooltipTemplate: FC<TooltipTemplateProps> = ({
  value,
  color,
  className
}) => {
  if (!value) {
    return null;
  }

  const renderValues = (data: SingleTooltipValue, index: number) => {
    const fill = color(data, index);

    return (
      <span className={css.subValue}>
        <span className={css.subValueColor} style={{ backgroundColor: fill }} />
        <span className={css.subValueName}>
          {formatValue(data.key || data.x)}:
        </span>
        <span>{formatValue(data.value || data.y)}</span>
      </span>
    );
  };

  const renderMultiple = (value: MultipleTooltipValues) => {
    const excessCount = value.data.length - 15;
    const pagedValues = value.data.slice(0, 15);

    return (
      <Fragment>
        {pagedValues.map((point, i) => (
          <Fragment key={i}>{renderValues(point, i)}</Fragment>
        ))}
        {excessCount > 0 && <div>...{excessCount} more...</div>}
      </Fragment>
    );
  };

  const isMultiple = Array.isArray((value as any).data);

  return (
    <div className={className} role="tooltip">
      <div className={css.label}>{formatValue(value!.x)}</div>
      <div className={css.value}>
        {isMultiple && renderMultiple(value as MultipleTooltipValues)}
        {!isMultiple && (
          <Fragment>
            {formatValue(
              (value as SingleTooltipValue).value ||
                (value as SingleTooltipValue).y
            )}
          </Fragment>
        )}
      </div>
    </div>
  );
};
import { TooltipTheme } from 'reablocks';

import css from './Tooltip.module.css';

export const tooltipTheme: TooltipTheme = {
  base: css.base,
  disablePointer: css.disablePointer
};
import React, { cloneElement, FC } from 'react';
import { Tooltip, TooltipProps } from 'reablocks';
import { TooltipTemplate } from './TooltipTemplate';
import { tooltipTheme } from './TooltipTheme';

export interface ChartTooltipProps extends TooltipProps {
  /**
   * Content for the tooltip.
   */
  content: any;

  /**
   * Tooltip data value.
   */
  value?: any;

  /**
   * Color scheme to apply.
   */
  color?: any;

  /**
   * Complete dataset.
   */
  data: any;

  /**
   * Whether the tooltip should move with the cursor or not.
   */
  followCursor?: boolean;
}

export const ChartTooltip: FC<Partial<ChartTooltipProps>> = ({
  content,
  value,
  data,
  color,
  ...rest
}) => (
  <Tooltip
    theme={tooltipTheme}
    {...rest}
    content={() => {
      if (!value && !data) {
        return null;
      }

      return typeof content === 'function'
        ? content(data || value, color)
        : cloneElement(content, {
          ...content.props,
          value,
          color
        });
    }}
  />
);

ChartTooltip.defaultProps = {
  content: <TooltipTemplate />
};
import React, {
  Fragment,
  ReactElement,
  useState,
  useRef,
  useCallback,
  useMemo,
  forwardRef,
  useImperativeHandle
} from 'react';
import { TooltipAreaEvent } from './TooltipAreaEvent';
import {
  ChartDataTypes,
  ChartInternalDataShape,
  ChartInternalShallowDataShape,
  ChartInternalNestedDataShape
} from '../data';
import {
  getPositionForTarget,
  getClosestContinousScalePoint,
  getClosestBandScalePoint
} from '../utils/position';
import { CloneElement, Placement } from 'reablocks';
import { ChartTooltip, ChartTooltipProps } from './ChartTooltip';
import { arc } from 'd3-shape';
import isEqual from 'react-fast-compare';
import { scaleLinear } from 'd3-scale';

export interface TooltipAreaProps {
  /**
   * Popperjs placement.
   */
  placement: Placement;

  /**
   * Chart height. Set internally.
   */
  height: number;

  /**
   * Chart width. Set internally.
   */
  width: number;

  /**
   * Chart D3 XScale. Set internally.
   */
  xScale: any;

  /**
   * Chart D3 YScale. Set internally.
   */
  yScale: any;

  /**
   * Whether the tooltip is disabled or not.
   */
  disabled: boolean;

  /**
   * Color setter.
   */
  color: any;

  /**
   * Chart internal data type.
   */
  data: ChartInternalDataShape[];

  /**
   * Child elements to be contained by.
   */
  children?: any;

  /**
   * Whether the area is radial or not.
   */
  isRadial?: boolean;

  /**
   * Whether the area is continous or not (e.g. line and area charts are continous, bar charts are not).
   */
  isContinous?: boolean;

  /**
   * Inner-radius to set the positioning by. Set internally.
   */
  innerRadius?: number;

  /**
   * Outer-radius to set the positioning by. Set internally.
   */
  outerRadius?: number;

  /**
   * Tooltip element.
   */
  tooltip: ReactElement<ChartTooltipProps, typeof ChartTooltip>;

  /**
   * Whether to inverse the data or not.
   */
  inverse: boolean;

  /**
   * When pointer entered mouse area.
   */
  onValueEnter: (event: TooltipAreaEvent) => void;

  /**
   * When pointer left mouse area.
   */
  onValueLeave: () => void;

  /**
   * Whether the layout is horizontal or not.
   */
  isHorizontal: boolean;

  /**
   * Start angle for the first value.
   */
  startAngle?: number;

  /**
   * End angle for the last value.
   */
  endAngle?: number;
}

interface TooltipDataShape {
  x?: ChartDataTypes;
  y?: ChartDataTypes;
  data?: ChartDataTypes | Array<ChartDataTypes | ChartInternalShallowDataShape>;
  i?: number;
}

// eslint-disable-next-line react/display-name
export const TooltipArea = forwardRef<any, Partial<TooltipAreaProps>>(
  (
    {
      children,
      inverse,
      tooltip,
      disabled,
      color,
      isRadial,
      isContinous,
      width,
      height,
      xScale,
      yScale,
      onValueEnter,
      data,
      isHorizontal,
      innerRadius,
      outerRadius,
      placement: placementProp,
      onValueLeave,
      startAngle,
      endAngle
    },
    childRef
  ) => {
    const [visible, setVisible] = useState<boolean>();
    const [placement, setPlacement] = useState<Placement>();
    const [value, setValue] = useState<any>();
    const [offsetX, setOffsetX] = useState<any>();
    const [offsetY, setOffsetY] = useState<any>();
    const [prevX, setPrevX] = useState<number>();
    const [prevY, setPrevY] = useState<number>();
    const ref = useRef<SVGRectElement | SVGPathElement | any>();
    const fullCircleref = useRef<SVGRectElement | SVGPathElement | any>(null);
    const isFullCircle = Math.abs(endAngle - startAngle) >= 2 * Math.PI;

    const range = Math.abs(endAngle - startAngle);

    const rotationFactor = 0.5;

    const getXCoord = useCallback(
      (x: number, y: number) => {
        // If the shape is radial, we need to convert the X coords to a radial format.
        if (isRadial) {
          const outerRadiusNew = outerRadius || Math.min(width, height) / 2;
          let rad =
            Math.atan2(y - outerRadiusNew, x - outerRadiusNew) +
            rotationFactor * Math.PI;

          // Align it with the expected start angle
          rad = (rad - startAngle) % (2 * Math.PI);

          // TODO: Figure out what the 'correct' way to do this is...
          if (rad < 0) {
            rad += Math.PI * 2;
          }

          // convert to given range
          const scale = scaleLinear()
            .domain([0, range])
            .range([startAngle, endAngle]);
          rad = scale(rad);

          return rad;
        }

        return x;
      },
      [endAngle, height, isRadial, outerRadius, range, startAngle, width]
    );

    const transformData = useCallback(
      (series: ChartInternalDataShape[]) => {
        const result: TooltipDataShape[] = [];

        if (inverse) {
          for (const point of series) {
            const seriesPoint = point as ChartInternalNestedDataShape;
            if (Array.isArray(seriesPoint.data)) {
              for (const nestedPoint of seriesPoint.data) {
                const right = nestedPoint.x;
                let idx = result.findIndex((r) => {
                  const left = r.x;
                  if (left instanceof Date && right instanceof Date) {
                    return left.getTime() === right.getTime();
                  }
                  return left === right;
                });

                if (idx === -1) {
                  result.push({
                    x: nestedPoint.x,
                    data: []
                  });

                  idx = result.length - 1;
                }

                const data = result[idx].data;

                if (Array.isArray(data)) {
                  data.push(nestedPoint);
                }
              }
            } else {
              result.push(point);
            }
          }
        } else {
          for (const point of series) {
            const nestedPoint = point as ChartInternalNestedDataShape;
            if (Array.isArray(nestedPoint.data)) {
              result.push({
                ...nestedPoint,
                x: nestedPoint.key,
                data: nestedPoint.data.map((d) => ({
                  ...d,
                  key: !isHorizontal ? d.x : d.y,
                  value: !isHorizontal ? d.y : d.x
                }))
              });
            } else {
              const shallowPoint = point as ChartInternalShallowDataShape;
              result.push({
                ...shallowPoint,
                // Histograms special logic...
                x: shallowPoint.key === undefined ? shallowPoint.x0 : point.key,
                y:
                  shallowPoint.value === undefined
                    ? shallowPoint.y
                    : shallowPoint.value
              });
            }
          }
        }

        return result;
      },
      [inverse, isHorizontal]
    );

    const onMouseMove = useCallback(
      (event: React.MouseEvent) => {
        const transformed = transformData(data);

        // Get our default placement
        let newPlacement = placementProp;
        if (!placementProp) {
          if (isHorizontal) {
            newPlacement = 'right';
          } else {
            newPlacement = 'top';
          }
        }

        // Get the path container element
        // Note that we are using the dummy 'full' circle for alignment
        let target = fullCircleref.current || ref.current;

        const { y, x } = getPositionForTarget({
          target: target,
          // Manually pass the x/y from the event
          clientX: event.clientX,
          clientY: event.clientY
        });

        // Need to flip scales/coords if we are a horz layout
        let keyScale;
        let valueScale;
        let coord;
        let attr = 'x';
        if (isHorizontal) {
          keyScale = yScale;
          valueScale = xScale;
          coord = y;
        } else {
          coord = getXCoord(x, y);
          keyScale = xScale;
          valueScale = yScale;
        }

        // If an index value exists in the data, use that to grab closest point
        if (typeof transformed[0].i === 'number') {
          attr = 'i';
        }

        // Get the closest point to the mouse
        // Consider invertable scales to be continous
        // Round non-continous charts with a continous scale down
        // Round band scales to the closest point for radial charts
        const newValue = keyScale.invert
          ? getClosestContinousScalePoint({
            pos: coord,
            scale: keyScale,
            data: transformed,
            attr,
            roundDown: !isContinous
          })
          : getClosestBandScalePoint({
            pos: coord,
            scale: keyScale,
            data: transformed,
            attr,
            roundClosest: isRadial
          });

        if (!isEqual(newValue, value) && newValue) {
          const pointX = keyScale(newValue.x);
          let pointY = valueScale(newValue.y);
          let marginX = 0;
          let marginY = 0;

          if (isNaN(pointY)) {
            pointY = height / 2;
            marginX = 10;
            if (!placement) {
              newPlacement = 'right';
            }
          } else {
            marginY = -10;
          }

          // If the points didn't change, don't trigger an update
          if (pointX === prevX && pointY === prevY) {
            return;
          }

          setPrevX(pointX);
          setPrevY(pointY);

          const target = event.target as SVGRectElement;
          const { top, left } = target.getBoundingClientRect();

          let offsetX = 0;
          let offsetY = 0;

          if (isRadial) {
            // If its radial, we need to convert the coords to radial format
            const outerRadius = Math.min(width, height) / 2;
            offsetX =
              pointY * Math.cos(pointX - rotationFactor * Math.PI) +
              outerRadius;
            offsetY =
              pointY * Math.sin(pointX - rotationFactor * Math.PI) +
              outerRadius;
          } else {
            offsetX = pointX;
            offsetY = pointY;
          }

          offsetX += left + marginX;
          offsetY += top + marginY;

          setPlacement(newPlacement);
          setVisible(true);
          setValue(newValue);
          setOffsetX(offsetX);
          setOffsetY(offsetY);

          onValueEnter({
            visible: true,
            value: newValue,
            pointY,
            pointX,
            offsetX,
            offsetY,
            nativeEvent: event
          });
        }
      },
      [
        data,
        getXCoord,
        height,
        isContinous,
        isHorizontal,
        isRadial,
        onValueEnter,
        placement,
        placementProp,
        prevX,
        prevY,
        transformData,
        value,
        width,
        xScale,
        yScale
      ]
    );

    const onMouseLeave = useCallback(() => {
      setPrevX(undefined);
      setPrevY(undefined);

      setValue(undefined);
      setVisible(false);

      onValueLeave();
    }, [onValueLeave]);

    useImperativeHandle(childRef, () => ({
      triggerMouseMove(e: React.MouseEvent) {
        onMouseMove(e);
      }
    }));

    const tooltipReference = useMemo(
      () => ({
        width: 4,
        height: 4,
        top: offsetY,
        left: offsetX
      }),
      [offsetX, offsetY]
    );

    const renderRadial = useCallback(() => {
      const innerRadiusNew = innerRadius || 0;
      const outerRadiusNew = outerRadius || Math.min(width, height) / 2;

      const d = arc()({
        innerRadius: innerRadiusNew,
        outerRadius: outerRadiusNew,
        startAngle: isFullCircle ? 0 : startAngle,
        endAngle: isFullCircle ? 2 * Math.PI : endAngle
      });

      // This is a dummuy full circle in the background as we need the
      // full circle to get the coordinates right from getBoundingClientRect().
      // If we don't use a full circle, then the bounding rectangle could be of any dimension and
      // the logic in getPositionForTarget() wouldn't work
      const fullCircle = arc()({
        innerRadius: innerRadiusNew,
        outerRadius: outerRadiusNew,
        startAngle: 0,
        endAngle: 2 * Math.PI
      });

      return (
        <>
          <path d={fullCircle!} opacity="0" cursor="auto" ref={fullCircleref} />
          <path
            d={d!}
            opacity="0"
            cursor="auto"
            ref={ref}
            onMouseMove={onMouseMove}
          />
        </>
      );
    }, [
      endAngle,
      height,
      innerRadius,
      isFullCircle,
      onMouseMove,
      outerRadius,
      startAngle,
      width
    ]);

    const renderLinear = useCallback(() => {
      return (
        <rect
          height={height}
          ref={ref}
          width={width}
          opacity={0}
          cursor="auto"
          onMouseMove={onMouseMove}
        />
      );
    }, [height, onMouseMove, width]);

    return (
      <Fragment>
        {disabled && children}
        {!disabled && (
          <g onMouseLeave={onMouseLeave} ref={childRef}>
            {isRadial && renderRadial()}
            {!isRadial && renderLinear()}
            <CloneElement<ChartTooltipProps>
              element={tooltip}
              visible={visible}
              placement={placement}
              modifiers={{
                offset: {
                  offset: '0, 15px'
                }
              }}
              reference={tooltipReference}
              color={color}
              value={value}
            />
            {children}
          </g>
        )}
      </Fragment>
    );
  }
);

TooltipArea.defaultProps = {
  isRadial: false,
  isContinous: true,
  tooltip: <ChartTooltip />,
  inverse: true,
  onValueEnter: () => undefined,
  onValueLeave: () => undefined,
  startAngle: 0,
  endAngle: 2 * Math.PI
};
import React, { PropsWithChildren, FC, useRef, useState, useEffect, useCallback } from 'react';
import bind from 'memoize-bind';
import {
  Pan,
  PanMoveEvent,
  PanStartEvent,
  PanEndEvent,
  PanCancelEvent
} from '../Gestures/Pan';
import { Zoom, ZoomEvent } from '../Gestures/Zoom';
import {
  identity,
  fromObject,
  fromDefinition,
  transform
} from 'transformation-matrix';
import isEqual from 'react-fast-compare';

export interface ZoomPanEvent {
  scale: number;
  x: number;
  y: number;
  type: 'zoom' | 'pan';
  nativeEvent: any;
}

export interface ZoomPanProps extends PropsWithChildren {
  height: number;
  width: number;
  scale: number;
  x: number;
  y: number;
  pannable: boolean;
  zoomable: boolean;
  disabled?: boolean;
  maxZoom: number;
  minZoom: number;
  zoomStep: number;
  constrain: boolean;
  globalPanning: boolean;
  disableMouseWheel?: boolean;
  requireZoomModifier?: boolean;
  onZoomPan: (event: ZoomPanEvent) => void;
  onZoom: (event: ZoomEvent) => void;
  onZoomEnd: () => void;
  onPanStart: (event: PanStartEvent) => void;
  onPanMove: (event: PanMoveEvent) => void;
  onPanEnd: (event: PanEndEvent) => void;
  onPanCancel: (event: PanCancelEvent) => void;
}

export const ZoomPan: FC<Partial<ZoomPanProps>> = ({
  height,
  width,
  children,
  disabled,
  pannable,
  maxZoom,
  minZoom,
  zoomable,
  scale,
  x,
  y,
  disableMouseWheel,
  constrain,
  zoomStep,
  onPanCancel,
  requireZoomModifier,
  globalPanning,
  onPanStart,
  onZoomPan,
  onPanMove,
  onPanEnd,
  onZoom,
  onZoomEnd
}) =>  {
  const zoomRef = useRef<Zoom>();
  const panRef = useRef<Pan>();
  const [isZooming, setIsZooming] = useState<boolean>();
  const [isPanning, setIsPanning] = useState<boolean>();
  const [matrix, setMatrix] = useState<any>(identity());

  useEffect(() => {
    const newMatrix = transform(
      fromDefinition([
        { type: 'translate', tx: x, ty: y },
        { type: 'scale', sx: scale, sy: scale },
      ])
    );

    if (!isEqual(newMatrix, matrix)) {
      setMatrix(newMatrix);
    }
  }, [x, y, scale, matrix]);


  const onPanStartHandler = useCallback((event: PanStartEvent) => {
    setIsPanning(true);
    onPanStart(event);
  }, [onPanStart]);

  const onPanMoveHandler =  useCallback((event: PanMoveEvent) => {
    onZoomPan({
      scale: scale,
      x: event.x,
      y: event.y,
      type: 'pan',
      nativeEvent: event.nativeEvent
    });

    onPanMove(event);
  }, [onPanMove, onZoomPan, scale]);

  const onPanEndHandler =  useCallback((event: PanEndEvent) => {
    setIsPanning(false);
    onPanEnd(event);
  }, [onPanEnd]);

  const onZoomHandler =  useCallback((event: ZoomEvent) => {
    onZoomPan({
      x: event.x,
      y: event.y,
      scale: event.scale,
      nativeEvent: event.nativeEvent,
      type: 'zoom'
    });
    onZoom(event);
  }, [onZoom, onZoomPan]);

  const onZoomEndHandler =  useCallback(() => {
    setIsZooming(false);
    onZoomEnd();
  }, [onZoomEnd]);

  const cursor = pannable ? 'move' : 'auto';
  const selection = isZooming || isPanning ? 'none' : 'auto';
  const matrixObj = fromObject(matrix);

  return (
    <Pan
      x={x}
      y={y}
      scale={scale}
      matrix={matrixObj}
      constrain={constrain}
      height={height}
      width={width}
      disabled={!pannable || disabled}
      ref={panRef}
      globalPanning={globalPanning}
      onPanStart={bind(onPanStartHandler)}
      onPanMove={bind(onPanMoveHandler)}
      onPanEnd={bind(onPanEndHandler)}
      onPanCancel={onPanCancel}
    >
      <Zoom
        ref={zoomRef}
        disabled={!zoomable || disabled}
        scaleFactor={zoomStep}
        disableMouseWheel={disableMouseWheel}
        maxZoom={maxZoom}
        minZoom={minZoom}
        scale={scale}
        x={x}
        y={y}
        style={{ cursor }}
        requireZoomModifier={requireZoomModifier}
        matrix={matrix}
        onZoom={bind(onZoomHandler)}
        onZoomEnd={bind(onZoomEndHandler)}
      >
        {!disabled && (
          <rect
            height={height}
            width={width}
            opacity={0}
            className="pan-container"
          />
        )}
        <g
          style={{
            pointerEvents: selection,
            userSelect: selection
          }}
        >
          {children}
        </g>
      </Zoom>
    </Pan>
  );
};

ZoomPan.defaultProps = {
  maxZoom: 10,
  minZoom: 0,
  zoomStep: 0.1,
  pannable: true,
  zoomable: true,
  constrain: true,
  height: 0,
  width: 0,
  x: 0,
  y: 0,
  scale: 1,
  globalPanning: true,
  onPanStart: () => undefined,
  onPanMove: () => undefined,
  onPanEnd: () => undefined,
  onPanCancel: () => undefined,
  onZoom: () => undefined,
  onZoomEnd: () => undefined
};
import { scaleLinear, scaleTime, scaleBand } from 'd3-scale';
import { getXDomain, getYDomain } from '../utils/domains';
import {
  ChartInternalShallowDataShape,
  ChartInternalNestedDataShape
} from '../data';
import { uniqueBy } from '../utils/array';

interface ScaleConfig {
  type: 'category' | 'value' | 'time' | 'duration';
  roundDomains?: boolean;
  data: any[];
  domain?: any[];
  padding?: number;
  scaled?: boolean;
  width?: number;
  height?: number;
  isMultiSeries?: boolean;
  isDiverging?: boolean;
}

/**
 * Gets the X Scale function.
 */
export function getXScale({
  type,
  roundDomains,
  data,
  width,
  domain,
  padding,
  scaled,
  isMultiSeries = false,
  isDiverging = false
}: ScaleConfig) {
  let scale;

  if (type === 'time' || type === 'duration' || type === 'value') {
    if (type === 'time') {
      scale = scaleTime().rangeRound([0, width!]);
    } else {
      scale = scaleLinear().rangeRound([0, width!]);
    }

    scale = scale.domain(domain || getXDomain({ data, scaled, isDiverging }));
  } else {
    if (!domain) {
