      y: yScale(yStartDomain),
      x: xScale(data!.x)
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data, yScale]);

  const fill = useMemo(
    () =>
      getColor({
        colorScheme: color,
        index,
        point: data
      }),
    [data, color, index]
  );

  const key = `symbol-${id}-${identifier(`${data!.id}`)}`;
  const ariaLabelData = useMemo(() => getAriaLabel(data), [data]);

  const isVisible = visible ? visible?.(data, index) : active;

  return (
    <Fragment>
      <g
        ref={rectRef}
        className={classNames({
          [css.inactive]: !active,
          [css.hidden]: !isVisible
        })}
        onMouseEnter={() => {
          setTooltipVisible(true);
          onMouseEnter(data!);
        }}
        onMouseLeave={() => {
          setTooltipVisible(false);
          onMouseLeave(data!);
        }}
        onClick={() => onClick(data!)}
        tabIndex={0}
        aria-label={ariaLabelData}
        role="graphics-document"
      >
        {symbol ? (
          <motion.g
            key={key}
            {...extras}
            initial={{
              translateX: exitProps.x,
              translateY: exitProps.y,
              opacity: 0
            }}
            animate={{
              translateX: enterProps.x,
              translateY: enterProps.y,
              opacity: 1
            }}
            exit={{
              translateX: exitProps.x,
              translateY: exitProps.y,
              opacity: 0
            }}
            transition={transitionProps}
          >
            {renderedSymbol}
          </motion.g>
        ) : (
          <motion.circle
            key={key}
            className={extras.className}
            style={{
              ...extras.style,
              ...generateGlowStyles({ glow }),
              cursor
            }}
            fill={fill}
            initial={{
              cx: exitProps.x,
              cy: exitProps.y,
              r,
              opacity: 0
            }}
            animate={{
              cx: enterProps.x,
              cy: enterProps.y,
              opacity: 1,
              r
            }}
            exit={{
              cx: exitProps.x,
              cy: exitProps.y,
              r,
              opacity: 0
            }}
            transition={transitionProps}
          />
        )}
      </g>
      {tooltip && !tooltip.props.disabled && (
        <CloneElement<ChartTooltipProps>
          element={tooltip}
          visible={tooltipVisible}
          reference={rectRef}
          value={data}
        />
      )}
    </Fragment>
  );
};

