export const Line: FC<Partial<LineProps>> = ({
  id,
  width,
  data,
  color,
  index,
  strokeWidth,
  hasArea,
  animated,
  yScale,
  xScale,
  showZeroStroke,
  interpolation,
  gradient,
  glow,
  ...rest
}) => {
  const [pathLength, setPathLength] = useState<number | null>(null);
  const ghostPathRef = useRef<SVGPathElement | null>(null);

  useEffect(() => {
    if (ghostPathRef.current) {
      setPathLength(ghostPathRef.current.getTotalLength());
    }
  }, [data, xScale, yScale, width]);

  const getLinePath = useCallback(
    (point: ChartInternalShallowDataShape[]) => {
      const fn = line()
        .x((d: any) => d.x)
        .y((d: any) => d.y1)
        .defined((d: any) => showZeroStroke || calculateShowStroke(d, point))
        .curve(interpolate(interpolation));

      return fn(point as any);
    },
    [interpolation, showZeroStroke]
  );

  const transition = useMemo(() => {
    if (animated) {
      return {
        ...DEFAULT_TRANSITION,
        delay: hasArea ? 0 : index * 0.05
      };
    } else {
      return {
        type: false,
        delay: 0
      };
    }
  }, [animated, hasArea, index]);

  const coords = useMemo(() => {
    return data.map((item: any) => ({
      x: xScale(item.x),
      x1: xScale(item.x) - xScale(item.x1),
      y: yScale(item.y),
      y0: yScale(item.y0),
      y1: yScale(item.y1)
    })) as ChartInternalShallowDataShape[];
  }, [data, xScale, yScale]);

  const enter = useMemo(() => {
    const linePath = getLinePath(coords);

    let strokeDasharray = '';
    if (!hasArea && pathLength !== null) {
      strokeDasharray = `${pathLength} ${pathLength}`;
    }

    return {
      d: linePath === null ? undefined : linePath,
      strokeDashoffset: 0,
      strokeDasharray: strokeDasharray
    };
  }, [coords, getLinePath, hasArea, pathLength]);

  const exit = useMemo(() => {
    let newCoords = coords;
    if (hasArea) {
      const maxY = Math.max(...yScale.range());
      newCoords = data.map((item: any) => ({
        x: xScale(item.x),
        x1: 0,
        y: maxY,
        y1: maxY,
        y0: maxY
      })) as ChartInternalShallowDataShape[];
    }

    const linePath = getLinePath(newCoords);

    let strokeDasharray = '';
    let strokeDashoffset = 0;
    if (!hasArea && pathLength !== null) {
      strokeDasharray = `${pathLength} ${pathLength}`;
      strokeDashoffset = pathLength;
    }

    return {
      d: linePath === null ? undefined : linePath,
      strokeDasharray,
      strokeDashoffset
    };
  }, [coords, data, getLinePath, hasArea, pathLength, xScale, yScale]);

  const stroke = color(data, index);
  const extras = constructFunctionProps(rest, data);
  const showLine = hasArea || pathLength !== null;

  const strokeFill = useMemo(() => {
    if (gradient) {
      return `url(#gradient-${id})`;
    }
    return stroke;
  }, [gradient, id]);

  // framer-motion freaks out when these are added for area
  if (hasArea) {
    delete enter.strokeDashoffset;
    delete exit.strokeDashoffset;
  }

  return (
    <Fragment>
      {showLine && (
        <MotionPath
          {...extras}
          pointerEvents="none"
          stroke={strokeFill}
          strokeWidth={strokeWidth}
          fill="none"
          transition={transition}
          custom={{
            enter,
            exit
          }}
          style={{
            ...extras.style,
            ...generateGlowStyles({ glow, colorSchemeColor: strokeFill })
          }}
        />
      )}
      {!hasArea && (
        <path opacity="0" d={enter.d} ref={ghostPathRef} pointerEvents="none" />
      )}
      {gradient && (
        <CloneElement<GradientProps>
          element={gradient}
          id={`gradient-${id}`}
          color={stroke}
        />
      )}
    </Fragment>
  );
};

Line.defaultProps = {
  showZeroStroke: true,
  strokeWidth: 3
};
import React, {
  Fragment,
  ReactElement,
  FC,
  useCallback,
  useState
} from 'react';
import { PointSeries, PointSeriesProps } from './PointSeries';
import { Area, AreaProps } from './Area';
import { MarkLine, MarkLineProps } from '../../common/MarkLine';
import {
  ChartInternalDataShape,
  ChartInternalNestedDataShape,
  ChartInternalShallowDataShape
} from '../../common/data';
import { CloneElement } from 'reablocks';
import {
  TooltipArea,
  TooltipAreaProps,
  TooltipAreaEvent
} from '../../common/Tooltip';
import { Line, LineProps } from './Line';
import { InterpolationTypes } from '../../common/utils/interpolation';
import { getColor, ColorSchemeType } from '../../common/color';
import { identifier } from 'safe-identifier';
import { LinearValueMarker, LinearValueMarkerProps } from '../../common';

export type AreaChartTypes =
  | 'standard'
  | 'grouped'
  | 'stacked'
  | 'stackedNormalized';

export interface AreaSeriesProps {
  /**
   * Id set internally by `AreaChart`.
   */
  id: string;

  /**
   * D3 scale for X Axis. Set internally by `AreaChart`.
   */
  xScale: any;

  /**
   * D3 scale for Y Axis. Set internally by `AreaChart`.
   */
  yScale: any;

  /**
   * Parsed data shape. Set internally by `AreaChart`.
   */
  data: ChartInternalDataShape[];

  /**
   * Height of the chart. Set internally by `AreaChart`.
   */
  height: number;

  /**
   * Width of the chart. Set internally by `AreaChart`.
   */
  width: number;

  /**
   * Whether to animate the enter/update/exit.
   */
  animated: boolean;

  /**
   * Type of area chart to render.
   */
  type: AreaChartTypes;

  /**
   * Interpolation type for the area/line.
   */
  interpolation: InterpolationTypes;

  /**
   * Tooltip for the chart area.
   */
  tooltip: ReactElement<TooltipAreaProps, typeof TooltipArea>;

  /**
   * Markline for the chart.
   */
  markLine: ReactElement<MarkLineProps, typeof MarkLine> | null;

  /**
   * Symbols used to show points.
   */
  symbols: ReactElement<PointSeriesProps, typeof PointSeries> | null;

  /**
   * Line that is rendered.
   */
  line: ReactElement<LineProps, typeof Line> | null;

  /**
   * Area that is rendered.
   */
  area: ReactElement<AreaProps, typeof Area> | null;

  /**
   * Color scheme for the series.
   */
  colorScheme: ColorSchemeType;

  /**
   * Whether the chart has been zoomed or not. Set internally by `AreaChart`.
   */
  isZoomed: boolean;

  /**
   * Value markers line for the chart.
   */
  valueMarkers:
    | ReactElement<LinearValueMarkerProps, typeof LinearValueMarker>[]
    | null;
}

// For area charts, often symbols exceed the area
// and we want to add a little bit of padding to prevent clipping
const PADDING = 25;
const HALF_PADDING = PADDING / 2;

export const AreaSeries: FC<Partial<AreaSeriesProps>> = ({
  data,
  height,
  id,
  width,
  isZoomed,
  tooltip,
  xScale,
  yScale,
  type,
  markLine,
  symbols,
  animated,
  area,
  interpolation,
  line,
  colorScheme,
  valueMarkers
}) => {
  const [activeValues, setActiveValues] = useState<any | null>(null);
  const [activePoint, setActivePoint] = useState<any | null>(null);

  const onValueEnter = useCallback((event: TooltipAreaEvent) => {
    setActivePoint(event.pointX);
    setActiveValues(event.value);
  }, []);

  const onValueLeave = useCallback(() => {
    setActivePoint(undefined);
    setActiveValues(undefined);
  }, []);

  const isMulti =
    type === 'grouped' || type === 'stacked' || type === 'stackedNormalized';

  const getPointColor = useCallback(
    (point, index: number) => {
      const key = Array.isArray(point) ? point?.[0]?.key : point?.key;

      return getColor({
        data,
        colorScheme,
        active: activeValues,
        point,
        index,
        key
      });
    },
    [activeValues, colorScheme, data]
  );

  const renderArea = useCallback(
    (data: ChartInternalShallowDataShape[], index = 0, total = 1) => (
      <Fragment>
        {line && (
          <CloneElement<LineProps>
            element={line}
            xScale={xScale}
            yScale={yScale}
            data={data}
            width={width}
            index={index}
            hasArea={area !== null}
            animated={animated}
            interpolation={interpolation}
            color={getPointColor}
          />
        )}
        {area && (
          <CloneElement<AreaProps>
            element={area}
            id={`${id}-area-${index}`}
            xScale={xScale}
            yScale={yScale}
            data={data}
            index={index}
            total={total}
            animated={animated}
            interpolation={interpolation}
            color={getPointColor}
          />
        )}
      </Fragment>
    ),
    [
      animated,
      area,
      getPointColor,
      id,
      interpolation,
      line,
      width,
      xScale,
      yScale
    ]
  );

  const renderSymbols = useCallback(
    (data: ChartInternalShallowDataShape[], index = 0) => {
      const visible = symbols !== null;
      const activeSymbols =
        (symbols && symbols.props.activeValues) || activeValues;

      // Animations are only valid for Area
      const isAnimated = area !== undefined && animated && !activeSymbols;

      return (
        <Fragment>
          {visible && (
            <CloneElement<PointSeriesProps>
              element={symbols}
              key={`point-series-${id}`}
              id={id}
              height={height}
              width={width}
              activeValues={activeSymbols}
              xScale={xScale}
              yScale={yScale}
              index={index}
              data={data}
              animated={isAnimated}
              color={() => getPointColor(data, index)}
            />
          )}
        </Fragment>
      );
    },
    [
      activeValues,
      animated,
      area,
      getPointColor,
      height,
      id,
      symbols,
      width,
      xScale,
      yScale
    ]
  );

  const renderMarkLine = useCallback(
    () => (
      <>
        {activeValues && markLine && (
          <CloneElement<MarkLineProps>
            element={markLine}
            height={height}
            pointX={activePoint}
          />
        )}
      </>
    ),
    [activePoint, activeValues, height, markLine]
  );

  const renderSingleSeries = useCallback(
    (data: ChartInternalShallowDataShape[]) => (
      <Fragment>
        {renderArea(data)}
        {renderMarkLine()}
        {renderSymbols(data)}
      </Fragment>
    ),
    [renderArea, renderMarkLine, renderSymbols]
  );

  const renderMultiSeries = useCallback(
    (data: ChartInternalNestedDataShape[]) => (
      <Fragment>
        {data
          .map((point, index) => (
            <Fragment key={identifier(`${point.key}`)}>
              {renderArea(point.data, index, data.length)}
            </Fragment>
          ))
          .reverse()}
        {renderMarkLine()}
        {data
          .map((point, index) => (
            <Fragment key={identifier(`${point.key}`)}>
              {renderSymbols(point.data, index)}
            </Fragment>
          ))
          .reverse()}
      </Fragment>
    ),
    [renderArea, renderMarkLine, renderSymbols]
  );

  const renderValueMarkers = useCallback(
    () => (
      <>
        {valueMarkers?.length &&
          valueMarkers.map((marker) => {
            const isVertical = marker?.props?.direction === 'vertical';
            const size = isVertical ? height : width;
            const value = isVertical
              ? xScale(marker.props.value)
              : yScale(marker.props.value);
            return (
              <CloneElement<LinearValueMarkerProps>
                key={marker.key}
                element={marker}
                size={size}
                value={value}
              />
            );
          })}
      </>
    ),
    [valueMarkers, width, yScale]
  );

  return (
    <Fragment>
      <defs>
        <clipPath id={`${id}-path`}>
          <rect
            width={isZoomed ? width : width + PADDING}
            height={height + PADDING}
            x={isZoomed ? 0 : -HALF_PADDING}
            y={-HALF_PADDING}
          />
        </clipPath>
      </defs>
      <CloneElement<TooltipAreaProps>
        element={tooltip}
        xScale={xScale}
        yScale={yScale}
        data={data}
        height={height}
        width={width}
        color={getPointColor}
        onValueEnter={onValueEnter}
        onValueLeave={onValueLeave}
      >
        <g clipPath={`url(#${id}-path)`}>
          {isMulti && renderMultiSeries(data as ChartInternalNestedDataShape[])}
          {!isMulti &&
            renderSingleSeries(data as ChartInternalShallowDataShape[])}
          {renderValueMarkers()}
        </g>
      </CloneElement>
    </Fragment>
  );
};

AreaSeries.defaultProps = {
  colorScheme: 'cybertron',
  animated: true,
  interpolation: 'linear',
  type: 'standard',
  line: <Line />,
  area: <Area />,
  markLine: <MarkLine />,
  tooltip: <TooltipArea />,
  symbols: <PointSeries />
