import React, { Fragment, ReactElement, FC, useCallback } from 'react';
import {
  geoNaturalEarth1,
  geoPath,
  GeoProjection,
  GeoPath,
  geoMercator
} from 'd3-geo';
import {
  ChartProps,
  ChartContainer,
  ChartContainerChildProps
} from '../common/containers/ChartContainer';
import { CloneElement } from 'reablocks';
