export interface SankeyLabelProps {
  /**
   * Whether the element is active or not. Set internally by `Sankey`.
   */
  active: boolean;

  /**
   * Whether the label is disabled. Set internally by `Sankey`.
   */
  disabled: boolean;

  /**
   * Width of the chart. Set internally by `Sankey`.
   */
