  );

  const enter = useMemo(
    () => ({
      d: getPath(data!),
      opacity: 1
    }),
    [data, getPath]
  );

  const exit = useMemo(() => {
    const [yStart] = yScale.domain();
    return {
      d: getPath(data!.map((d) => ({ ...d, y: yStart }))),
      opacity: 0
    };
  }, [data, yScale, getPath]);

  return (
    <MotionPath
      custom={{
        enter,
        exit
      }}
      transition={transition}
      className={className}
      pointerEvents="none"
      stroke={fill}
      fill="none"
      strokeWidth={strokeWidth}
    />
  );
};

