            }
            onClick={(event) =>
              onClick?.({
                value: data,
                nativeEvent: event
              })
            }
          />
        </Fragment>
      );
    },
    [
      active,
      className,
      data,
      getArc,
      getFill,
      guide,
      onClick,
      onMouseEnter,
      onMouseLeave,
      transition,
      yScale
    ]
  );

  return (
    <Fragment>
      {renderBar(currentColorShade)}
      {gradient && <Gradient id={`${id}-gradient`} color={currentColorShade} />}
    </Fragment>
  );
};

