  ChartNestedDataShape,
  ChartShallowDataShape
} from '../../common/data';
import { ColorSchemeType, getColor } from '../../common/color';
import { RadialGaugeArc, RadialGaugeArcProps } from './RadialGaugeArc';
import {
  RadialGaugeStackedArc,
  RadialGaugeStackedArcProps
