export interface VennArcProps {
  /**
   * Whether the shape is active or not.
   */
  active?: boolean | null;

  /**
   * Id set by the parent.
   */
  id: string;

  /**
   * Whether the chart is animated or not.
   */
  animated?: boolean;

  /**
   * Fill color for the arc.
   */
  fill: string;

  /**
   * Stroke for the arc.
   */
  stroke?:
    | string
    | ((
        data: IVennLayout<any>[],
        index: number,
        isActive: boolean | null,
        isHovered: boolean | null
      ) => string);

  /**
   * The internal data object built by venn.js
   */
  data: IVennLayout<any>;

  /**
   * Whether the chart is disabled.
   */
  disabled?: boolean;

  /**
   * Stroke on the arc.
   */
  strokeWidth?: number;

  /**
   * Initial style of arc.
   */
  initialStyle?: any;

  /**
   * Active style of arc.
   */
  activeStyle?: any;

  /**
   * Inactive style of arc.
   */
  inactiveStyle?: any;

  /**
   * CSS Styles for the arc.
   */
  style?: any;

  /**
   * Tooltip element.
   */
  tooltip?: ReactElement<ChartTooltipProps, typeof ChartTooltip> | null;

  /**
   * Mask element for the arc.
   */
  mask: ReactElement<MaskProps, typeof Mask> | null;

  /**
   * Gradient shades for the arc.
   */
  gradient: ReactElement<GradientProps, typeof Gradient> | null;

  /**
   * Glow styling for the arc.
   */
  glow: Glow;

  /**
   * Event for when the arc is clicked.
   */
  onClick: (event) => void;

  /**
   * Event for when the arc has mouse enter.
   */
  onMouseEnter: (event) => void;

  /**
   * Event for when the arc has mouse leave.
   */
  onMouseLeave: (event) => void;
}

export const VennArc: FC<Partial<VennArcProps>> = ({
  data,
  fill,
  disabled,
  animated,
  stroke,
  mask,
  id,
  style,
  active,
  inactiveStyle,
  activeStyle,
  initialStyle,
  strokeWidth,
  gradient,
  glow,
  tooltip,
  onClick,
  onMouseEnter,
  onMouseLeave
}) => {
  const [internalActive, setInternalActive] = useState<boolean>(false);
  const arcRef = useRef<any | null>(null);
  const { transition, d } = useInterpolate({ animated, data });
  const currentStyle = active
    ? activeStyle
    : active === null
      ? inactiveStyle
      : initialStyle;

  const arcFill =
    gradient && !mask
      ? `url(#gradient-${id})`
      : mask
        ? `url(#mask-pattern-${id})`
        : fill;

  const { pointerOut, pointerOver } = useHoverIntent({
    onPointerOver: (event) => {
      if (!disabled) {
        setInternalActive(true);
        onMouseEnter?.({
          value: data.data,
          nativeEvent: event
        });
      }
    },
    onPointerOut: (event) => {
      if (!disabled) {
        setInternalActive(false);
        onMouseLeave?.({
          value: data.data,
          nativeEvent: event
        });
      }
    }
  });

  const tooltipData = useMemo(
    () => ({ y: data.data.size, x: data.data?.sets?.join(' | ') }),
    [data]
  );
  const ariaLabelData = useMemo(() => getAriaLabel(tooltipData), [tooltipData]);

  return (
    <g
      title={data.data.key}
      onPointerOver={pointerOver}
      onPointerOut={pointerOut}
      onClick={(event) => {
        if (!disabled) {
          onClick?.({
            value: data.data,
            nativeEvent: event
          });
        }
      }}
      tabIndex={0}
      aria-label={ariaLabelData}
      role="graphics-document"
    >
      <motion.path
        ref={arcRef}
        fill={arcFill}
        id={`${id}-arc`}
        strokeWidth={strokeWidth}
        stroke={stroke as string}
        transition={transition}
        d={d}
        initial={initialStyle}
        animate={currentStyle}
        style={{
          ...style,
          ...generateGlowStyles({
            glow,
            colorSchemeColor: typeof stroke === 'string' ? stroke : null
          })
        }}
      />
      {mask && (
        <Fragment>
          <Mask id={`mask-${id}`} fill={`url(#gradient-${id})`} />
          <CloneElement<MaskProps>
            element={mask}
            id={`mask-pattern-${id}`}
            fill={fill}
          />
        </Fragment>
      )}
      {gradient && (
        <CloneElement<GradientProps>
          element={gradient}
          id={`gradient-${id}`}
          color={fill}
        />
      )}
      {tooltip && !tooltip.props.disabled && (
        <CloneElement<ChartTooltipProps>
          element={tooltip}
          visible={!!internalActive}
          reference={arcRef}
          value={tooltipData}
        />
      )}
    </g>
  );
};

