export const BubbleLabel: FC<Partial<BubbleLabelProps>> = ({
  id,
  data,
  format,
  wrap,
  fill,
  fontSize,
  fontFamily,
  animated
}) => {
  const transition = animated ? DEFAULT_TRANSITION : { type: false, delay: 0 };
  let isElement = false;
  let label;

  if (format) {
    label = format(data);
    isElement = isValidElement(label);
  }

  if (!isElement) {
    const text = wrap
      ? wrapText({
        key: data.data.key,
        fontFamily,
        fontSize,
        width: data.r
      })
      : data.data.key;

    return (
      <motion.text
        initial={{
          x: data.x,
          y: data.y
        }}
        animate={{
          x: data.x,
          y: data.y
        }}
        transition={transition}
        id={`${id}-text`}
        style={{ pointerEvents: 'none', fontFamily, fontSize }}
        fill={fill}
        textAnchor="middle"
      >
        {text}
      </motion.text>
    );
  }

  return (
    <g style={{ transform: `translate(${data.x}px, ${data.y}px)` }}>{label}</g>
  );
};

