        <Fragment key={index}>
          {line && index !== 0 && (
            <CloneElement<FunnelAxisLineProps>
              element={line}
              index={index}
              xScale={xScale}
              yScale={yScale}
            />
          )}
          {label && (
            <CloneElement<FunnelAxisLabelProps>
              element={label}
              index={index}
              data={data[index]}
              xScale={xScale}
              yScale={yScale}
            />
          )}
        </Fragment>
      ))}
    </>
  );
};

