import React, { useState } from "react";
import { Link } from 'react-router-dom';

export function EditProfile({ profile, setProfile, mbtiOption }) {
    const [formData, setFormData] = useState(profile);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = () => {
        setProfile(formData);
    };

    return (
        <main>
            <section className="profile-container">
            <Link to="/editmbti"><img className="editpercent" src="./img/calc.png" alt="a calculator" /></Link>
                <div className="profile-pic">edit picture</div>
                <div className="profile-info">
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="name">Name:</label><br />
                        <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} /><br />
                        <label>Pronouns:</label><br />
                        <input type="text" id="pronouns" name="pronouns" value={formData.pronouns} onChange={handleChange} /><br /><br />
                        <select id="types" name="mbti" value={formData.mbti} onChange={handleChange}>
                            <option value="starter">Edit MBTI</option>
                            {mbtiOption.map((option) => (
                                <option key={option.value} value={option.value}>{option.label}</option>
                            ))}
                        </select>
                        <input type="text" placeholder="Edit Pet Peeve" className="edit" id="petpeeve" name="petpeeve" value={formData.petpeeve} onChange={handleChange}></input>
                        <input type="text" placeholder="Edit Hobby" className="edit" id="hobby" name="hobby" value={formData.hobby} onChange={handleChange}></input>
                        <input type="text" placeholder="Edit Favorite Song" className="edit" id="favsong" name="favsong" value={formData.favsong} onChange={handleChange}></input>
                        {/* <img className="linksong" src="./img/link.png" alt="a link icon" /> */}
                        <Link to="/profile">
                            <input type="button" value="Submit" onClick={handleSubmit} />
                        </Link>
                    </form>
                </div>
            </section>
        </main>
    );
}
