import React from "react";
import { useEffect } from "react";
import { getAuth, EmailAuthProvider, GoogleAuthProvider, onAuthStateChanged } from 'firebase/auth';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import { useNavigate } from "react-router-dom";
import { getDatabase, ref, set } from 'firebase/database';


export function SignIn(props) {

    const firebaseUIConfig = {
        signInOptions: [GoogleAuthProvider.PROVIDER_ID,
            EmailAuthProvider.PROVIDER_ID
        ],
        signInFlow: 'popup', 
        credentialHelper: 'none', 
        callbacks: { 
          signInSuccessWithAuthResult: () => {
            return false;
          }
        }
      }

    const navigate = useNavigate();
    const auth = getAuth();
    const database = getDatabase();

    useEffect(() => {
        const changeState = onAuthStateChanged(auth, user => {
            if (user) {
                navigate('/profile');
            }
        });
        return () => changeState();
    }, [navigate]);


    return (
        <div className="login-container">
            <div className="welcome-box"><b>Welcome to teaMBTI</b></div>
            <StyledFirebaseAuth uiConfig={firebaseUIConfig} firebaseAuth={auth} />
            <div className="create-account">
                <p className="sign-in-tips">If you do not have an account before, it will automatically sign up for you.</p>
            </div>
        </div>
    );
}
