import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { Compatability } from './compatability';

function Teams({ profile }) {
    const [teamName, setTeamName] = useState('');
    const [teamList, setTeamList] = useState([]);
    const [currentTeamIndex, setCurrentTeamIndex] = useState(null);
    const [currentProfileIndex, setCurrentProfileIndex] = useState(null);
    const { teamName: paramTeamName } = useParams();
    const [showMbtiForm, setShowMbtiForm] = useState(false);
    const [mbtiData, setMbtiData] = useState({
        EI: '',
        SN: '',
        TF: '',
        JP: ''
    });
    const [selectedMember, setSelectedMember] = useState(null);
    const [selectedUser, setSelectedUser] = useState(null);
    const [showCharts, setShowCharts] = useState(false);

    useEffect(() => {
        if (paramTeamName) {
            const teamIndex = teamList.findIndex(team => team.teamName === paramTeamName);
            if (teamIndex !== -1) {
                setCurrentTeamIndex(teamIndex);
                setTeamName(paramTeamName);
            }
        }
    }, [paramTeamName, teamList]);

    const handleAddTeam = () => {
        if (teamName) {
            setTeamList([...teamList, { teamName, profiles: [{ ...profile, isDefaultProfile: true, mbtiData: { EI: '', SN: '', TF: '', JP: '' } }] }]);
            setTeamName('');
            setCurrentTeamIndex(teamList.length);
        }
    };

    const handleAddProfile = () => {
        if (currentTeamIndex !== null) {
            const currentProfiles = teamList[currentTeamIndex].profiles;
            if (currentProfiles.length < 6) {
                const newProfile = {
                    name: 'Name',
                    mbti: 'MBTI',
                    petpeeve: 'Pet Peeve',
                    hobby: 'Hobby',
                    favsong: 'Favorite Song',
                    isEditing: true,
                    isDefaultProfile: false,
                    mbtiData: {
                        EI: '',
                        SN: '',
                        TF: '',
                        JP: ''
                    }
                };
                const updatedTeamList = [...teamList];
                updatedTeamList[currentTeamIndex].profiles = [...currentProfiles, newProfile];
                setTeamList(updatedTeamList);
            }
        }
    };

    const handleInputChange = (index, field, value) => {
        const updatedTeamList = [...teamList];
        updatedTeamList[currentTeamIndex].profiles[index][field] = value;
        setTeamList(updatedTeamList);
    };

    const handleSave = (index) => {
        const updatedTeamList = [...teamList];
        updatedTeamList[currentTeamIndex].profiles[index].isEditing = false;
        setTeamList(updatedTeamList);
    };

    const handleEdit = (index) => {
        const updatedTeamList = [...teamList];
        updatedTeamList[currentTeamIndex].profiles[index].isEditing = true;
        setTeamList(updatedTeamList);
    };

    const handleDelete = (index) => {
        const updatedTeamList = [...teamList];
        updatedTeamList[currentTeamIndex].profiles = updatedTeamList[currentTeamIndex].profiles.filter((_, i) => i !== index);
        setTeamList(updatedTeamList);
    };

    const handleTeamSelect = (index) => {
        setCurrentTeamIndex(index);
        setTeamName(teamList[index].teamName);
    };

    const handleMbtiInputChange = (e) => {
        const { name, value } = e.target;
        setMbtiData({
            ...mbtiData,
            [name]: value
        });
    };

    const handleMbtiFormSubmit = (e) => {
        e.preventDefault();
        const updatedTeamList = [...teamList];
        if (currentProfileIndex !== null) {
            updatedTeamList[currentTeamIndex].profiles[currentProfileIndex].mbtiData = mbtiData;
            setTeamList(updatedTeamList);
            setShowMbtiForm(false);
        }
    };

    const handleRunTest = () => {
        setShowCharts(true);
    };

    return (
        <main>
            <header>
                <input
                    id="team-name"
                    type="text"
                    placeholder="Team Name..."
                    value={teamName}
                    onChange={(e) => setTeamName(e.target.value)}
                />
                <img
                    className="checkmark"
                    src="../img/check.png"
                    alt="checkmark icon"
                    onClick={handleAddTeam}
                />
                {currentTeamIndex !== null && (
                    <div className="profile" onClick={handleAddProfile}>
                        <div id="circle">
                            <img src="../img/plus.png" alt="addition icon" />
                        </div>
                    </div>
                )}
            </header>
            <div className="rect">
                {currentTeamIndex !== null && teamList[currentTeamIndex].profiles.map((profile, index) => (
                    <Profile
                        key={index}
                        profile={profile}
                        index={index}
                        onInputChange={handleInputChange}
                        onSave={handleSave}
                        onEdit={handleEdit}
                        onDelete={handleDelete}
                        setShowMbtiForm={setShowMbtiForm}
                        setCurrentProfileIndex={setCurrentProfileIndex}
                    />
                ))}
            </div>
            <div className="rectteamslist">
                <h3>My Teams</h3>
                {teamList.map((team, index) => (
                    <Link key={index} to={"/teams/" + team.teamName} onClick={() => handleTeamSelect(index)}>
                        <h4 style={{ top: (25 + index * 10) + '%' }}>{team.teamName}</h4>
                    </Link>
                ))}
                <a><img src="../img/plus.png" alt="addition icon" /></a>
            </div>
            <h2 className="comph2">Compatibility</h2>
            <h3 className="comph3">Create New Compatibility Assessment</h3>
            <div className="createform">
                {currentTeamIndex !== null && (
                    <form className="team" onSubmit={(e) => { e.preventDefault(); handleRunTest(); }}>
                        <select id="members" name="members" onChange={(e) => setSelectedMember(e.target.value)}>
                            <option value="starter" disabled>Select Member #1</option>
                            {teamList[currentTeamIndex].profiles.map((profile, index) => (
                                <option key={index} value={index}>
                                    {profile.name}
                                </option>
                            ))}
                        </select>
                        <select id="members2" name="members2" onChange={(e) => setSelectedUser(e.target.value)}>
                            <option value="starter" disabled>Select Member #2</option>
                            {teamList[currentTeamIndex].profiles.map((profile, index) => (
                                <option key={index} value={index}>
                                    {profile.name}
                                </option>
                            ))}
                        </select>
                        <input className="compsubmit" type="submit" value="Run Test" />
                    </form>
                )}
            </div>
            {showCharts && (
                <div className="compatibility-charts">
                    <Compatability
                        userMbti={profile.mbtiData}
                        memberMbti={teamList[currentTeamIndex].profiles[selectedMember]?.mbtiData}
                        userName={profile.name} // Use the name from the user's profile
                        memberName={teamList[currentTeamIndex].profiles[selectedUser]?.name}
                    />
                </div>
            )}
            <div>
                {showMbtiForm && (
                    <form className="mbti-form" onSubmit={handleMbtiFormSubmit}>
                        <div className="input-group">
                            <label htmlFor="EI">E/I Percentage:</label>
                            <input
                                type="number"
                                id="EI"
                                name="EI"
                                value={mbtiData.EI}
                                onChange={handleMbtiInputChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="SN">S/N Percentage:</label>
                            <input
                                type="number"
                                id="SN"
                                name="SN"
                                value={mbtiData.SN}
                                onChange={handleMbtiInputChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="TF">T/F Percentage:</label>
                            <input
                                type="number"
                                id="TF"
                                name="TF"
                                value={mbtiData.TF}
                                onChange={handleMbtiInputChange}
                                required
                            />
                        </div>
                        <div className="input-group">
                            <label htmlFor="JP">J/P Percentage:</label>
                            <input
                                type="number"
                                id="JP"
                                name="JP"
                                value={mbtiData.JP}
                                onChange={handleMbtiInputChange}
                                required
                            />
                        </div>
                        <button type="submit" className="submit-button">Submit</button>
                    </form>
                )}
            </div>
        </main>
    );
}

function Profile({ profile, index, onInputChange, onSave, onEdit, onDelete, setShowMbtiForm, setCurrentProfileIndex }) {
    let profileContent;

    if (profile.isEditing) {
        profileContent = (
            <div className="profile-content">
                <input
                    type="text"
                    value={profile.name}
                    placeholder="Name"
                    onChange={(e) => onInputChange(index, 'name', e.target.value)}
                />
                <input
                    type="text"
                    value={profile.mbti}
                    placeholder="MBTI"
                    onChange={(e) => onInputChange(index, 'mbti', e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Pet Peeve"
                    value={profile.petpeeve}
                    onChange={(e) => onInputChange(index, 'petpeeve', e.target.value)}
                />
                <input
                    type="text"
                    value={profile.hobby}
                    placeholder="Hobby"
                    onChange={(e) => onInputChange(index, 'hobby', e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Favorite Song"
                    value={profile.favsong}
                    onChange={(e) => onInputChange(index, 'favsong', e.target.value)}
                />
                <button className="savebutton" onClick={() => onSave(index)}>Save</button>
            </div>
        );
    } else {
        profileContent = (
            <div className="profile-content">
                <h5>{profile.name}</h5>
                <button className="tag" id="mbti">{profile.mbti}</button>
                <button className="tag" id="petpeeve">{profile.petpeeve}</button>
                <button className="tag" id="hobby">{profile.hobby}</button>
                <button className="tag" id="favsong">{profile.favsong}</button>
                <button className="editbutton" onClick={() => onEdit(index)}>Edit</button>
                <button className="calcbutton" onClick={() => { setShowMbtiForm(true); setCurrentProfileIndex(index); }}>
                    <img src="../img/editmbti.png" alt="Edit MBTI" />
                </button>
            </div>
        );
    }

    return (
        <div className="newprofile">
            <div id="newcircle"></div>
            {!profile.isDefaultProfile && <button className="delbutton" onClick={() => onDelete(index)}><img src="../img/trash.png" alt="Delete" /></button>}
            {profileContent}
        </div>
    );
}

export { Teams };
